import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import Table from '../components/Table';
import Button from '../components/Button';
import PrintButton from '../components/PrintButton';
import ReturnModal from '../components/ReturnModal';
import './SalesManagement.css';

const SalesManagement = () => {
  const { user } = useAuth();
  const [sales, setSales] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedSale, setSelectedSale] = useState(null);
  const [loadingSale, setLoadingSale] = useState(false);
  const [deleteFilters, setDeleteFilters] = useState({
    customer_name: '',
    start_date: '',
    end_date: '',
    status: 'completed'
  });
  const [editFormData, setEditFormData] = useState({
    items: []
  });
  const [products, setProducts] = useState([]);
  const [filters, setFilters] = useState({
    status: '',
    customer_name: '',
    start_date: '',
    end_date: ''
  });
  const [showReturnModal, setShowReturnModal] = useState(false);
  const [selectedSaleForReturn, setSelectedSaleForReturn] = useState(null);
  const [saleItemsForReturn, setSaleItemsForReturn] = useState([]);
  const [loadingReturnData, setLoadingReturnData] = useState(false);
  const [returns, setReturns] = useState([]);
  const [loadingReturns, setLoadingReturns] = useState(false);
  const [activeTab, setActiveTab] = useState('sales'); // 'sales' or 'returns'

  const fetchSales = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      const params = new URLSearchParams();

      if (filters.status) params.append('status', filters.status);
      if (filters.customer_name) params.append('search', filters.customer_name);
      if (filters.start_date) params.append('created_at__date__gte', filters.start_date);
      if (filters.end_date) params.append('created_at__date__lte', filters.end_date);

      // Fetch all sales (including returns)
      const salesResponse = await api.get(`/sales/?${params.toString()}`);
      const salesData = salesResponse?.data?.results || salesResponse?.data || [];

      // Mark sales and returns with type field based on sale_type
      const combinedData = (salesData || []).map(sale => ({
        ...sale,
        type: sale.sale_type === 'return' ? 'return' : 'sale'
      }));

      // Sort by creation date (newest first)
      combinedData.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));

      setSales(combinedData);
      setReturns(combinedData.filter(item => item.type === 'return'));
    } catch (err) {
      setError('Failed to fetch sales: ' + (err.response?.data?.error || err.message));
      console.error('Sales fetch error:', err);
    } finally {
      setLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    fetchSales();
    fetchProducts();
  }, [fetchSales]);

  const fetchReturns = async () => {
    try {
      setLoadingReturns(true);
      setError('');
      const response = await api.get('/sales/returns/');
      setReturns(response.data.results || response.data);
    } catch (err) {
      setError('Failed to fetch returns: ' + (err.response?.data?.error || err.message));
      console.error('Returns fetch error:', err);
    } finally {
      setLoadingReturns(false);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await api.get('/products/?is_active=true');
      setProducts(response.data.results || response.data);
    } catch (err) {
      console.error('Products fetch error:', err);
    }
  };

  const handleDeleteSales = async () => {
    try {
      setError('');
      const response = await api.delete('/sales/delete/', {
        data: deleteFilters
      });

      alert(`Successfully deleted ${response.data.deleted_count} sales`);
      setShowDeleteModal(false);
      setDeleteFilters({
        customer_name: '',
        start_date: '',
        end_date: '',
        status: 'completed'
      });
      fetchSales();
      fetchProducts(); // Refresh products to update stock quantities
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to delete sales');
      console.error('Delete sales error:', err);
    }
  };

  const handleCreateReturn = async (returnData) => {
    try {
      setError('');
      await api.post('/sales/', returnData);
      alert('Return created successfully');
      setShowReturnModal(false);
      setSelectedSaleForReturn(null);
      setSaleItemsForReturn([]);
      fetchSales();
      fetchProducts(); // Refresh products to update stock quantities
    } catch (err) {
      throw new Error(err.response?.data?.error || 'Failed to create return');
    }
  };

  const completeReturn = async (returnId) => {
    try {
      setError('');
      await api.post(`/sales/${returnId}/complete-return/`);
      alert('Return completed successfully');
      fetchSales();
      fetchProducts(); // Refresh products to update stock quantities
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to complete return');
      console.error('Complete return error:', err);
    }
  };

  const handleEditSale = async () => {
    try {
      setError('');
      await api.put(`/sales/${selectedSale.id}/edit/`, editFormData);
      alert('Sale updated successfully');
      setShowEditModal(false);
      setSelectedSale(null);
      setEditFormData({ items: [] });
      fetchSales();
      fetchProducts(); // Refresh products to update stock quantities
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to update sale');
      console.error('Edit sale error:', err);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleDeleteFilterChange = (e) => {
    const { name, value } = e.target;
    setDeleteFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const clearFilters = () => {
    setFilters({
      status: '',
      customer_name: '',
      start_date: '',
      end_date: ''
    });
  };

  const openReturnModal = async (sale) => {
    try {
      setLoadingReturnData(true);
      setError('');

      // Fetch sale items for return
      const response = await api.get(`/sales/${sale.id}/items-for-return/`);
      const returnData = response.data;

      setSelectedSaleForReturn(returnData.sale);
      setSaleItemsForReturn(returnData.items);
      setShowReturnModal(true);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to load sale data for return');
      console.error('Return modal error:', err);
    } finally {
      setLoadingReturnData(false);
    }
  };

  const openEditModal = async (sale) => {
    try {
      setLoadingSale(true);
      setError('');

      // Fetch full sale data including items
      const response = await api.get(`/sales/${sale.id}/`);
      const fullSale = response.data;

      setSelectedSale(fullSale);
      setEditFormData({
        items: fullSale.items.map(item => ({
          product: item.product,
          quantity: item.quantity,
          unit: item.unit?.id || item.unit || '',
          unit_price: item.unit_price,
          price_mode: item.price_mode || 'standard'
        }))
      });
      setShowEditModal(true);
    } catch (err) {
      setError('Failed to load sale details');
      console.error('Sale fetch error:', err);
    } finally {
      setLoadingSale(false);
    }
  };

  const addEditItem = () => {
    setEditFormData(prev => ({
      ...prev,
      items: [...prev.items, { product: '', quantity: 1, unit: '', unit_price: 0 }]
    }));
  };

  const removeEditItem = (index) => {
    setEditFormData(prev => ({
      ...prev,
      items: prev.items.filter((_, i) => i !== index)
    }));
  };

  const updateEditItem = (index, field, value) => {
    setEditFormData(prev => ({
      ...prev,
      items: prev.items.map((item, i) => 
        i === index ? { ...item, [field]: value } : item
      )
    }));
  };

  const validateEditSale = () => {
    if (!editFormData.items || editFormData.items.length === 0) {
      return false;
    }

    // Check if any item has invalid quantity
    for (const item of editFormData.items) {
      const quantity = parseFloat(item.quantity);
      if (isNaN(quantity) || quantity <= 0) {
        return false;
      }

      // Find the corresponding original item to check stock availability
      const originalItem = selectedSale?.items?.find(original => 
        original.product === item.product && 
        original.unit === item.unit && 
        original.price_mode === item.price_mode
      );

      if (originalItem) {
        // Calculate the quantity difference
        const originalQuantity = parseFloat(originalItem.quantity);
        const quantityDiff = quantity - originalQuantity;

        // If increasing quantity, check stock availability
        if (quantityDiff > 0) {
          // Find the product to get current stock
          const product = products.find(p => p.id === parseInt(item.product));
          if (product) {
            // Convert quantity difference to base units for stock check
            // This is a simplified check - in a real scenario, you'd need proper unit conversion
            const stockNeeded = quantityDiff;
            if (product.stock_quantity < stockNeeded) {
              return false;
            }
          }
        }
      }
    }

    return true;
  };

  const getProductPrice = (productId) => {
    const product = products.find(p => p.id === parseInt(productId));
    return product ? product.price : 0;
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      pending: 'status-pending',
      completed: 'status-completed',
      cancelled: 'status-cancelled',
      returned: 'status-returned'
    };
    return <span className={`status-badge ${statusClasses[status] || ''}`}>{status}</span>;
  };

  // Check if user has permission to manage sales
  const canManageSales = user?.role === 'admin' || user?.role === 'manager';

  if (!canManageSales) {
    return (
      <div className="sales-management">
        <div className="access-denied">
          <h2>Access Denied</h2>
          <p>You don't have permission to manage sales.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="sales-management">
      <div className="page-header">
        <h1>Sales Management</h1>
        <div className="header-actions">
          <PrintButton
            data={{
              ...sales,
              user_name: user?.username || 'Unknown User',
              user_id: user?.id || 'unknown',
              print_timestamp: new Date().toISOString(),
              print_id: `PRINT-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
            }}
            title="Sales Management Report"
            type="sales_history"
            printText="Print Sales Report"
            className="print-sales-report-btn"
          />
          <Button 
            variant="danger" 
            onClick={() => setShowDeleteModal(true)}
          >
            Delete Sales
          </Button>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError('')}>&times;</button>
        </div>
      )}

      {/* Tabs */}
      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'sales' ? 'active' : ''}`}
          onClick={() => setActiveTab('sales')}
        >
          Sales
        </button>
        <button 
          className={`tab ${activeTab === 'returns' ? 'active' : ''}`}
          onClick={() => setActiveTab('returns')}
        >
          Returns
        </button>
      </div>

      {/* Filters */}
      <div className="filters-section">
        <h3>Filters</h3>
        <div className="filters-grid">
          <div className="form-group">
            <label>Status</label>
            <select
              name="status"
              value={filters.status}
              onChange={handleFilterChange}
            >
              <option value="">All Statuses</option>
              <option value="pending">Pending</option>
              <option value="completed">Completed</option>
              <option value="cancelled">Cancelled</option>
              <option value="returned">Returned</option>
            </select>
          </div>

          <div className="form-group">
            <label>Customer Name</label>
            <input
              type="text"
              name="customer_name"
              value={filters.customer_name}
              onChange={handleFilterChange}
              placeholder="Search by customer name"
            />
          </div>

          <div className="form-group">
            <label>Start Date</label>
            <input
              type="date"
              name="start_date"
              value={filters.start_date}
              onChange={handleFilterChange}
            />
          </div>

          <div className="form-group">
            <label>End Date</label>
            <input
              type="date"
              name="end_date"
              value={filters.end_date}
              onChange={handleFilterChange}
            />
          </div>
        </div>

        <div className="filter-actions">
          <Button variant="secondary" onClick={clearFilters}>
            Clear Filters
          </Button>
        </div>
      </div>

      {/* Sales Table */}
      {activeTab === 'sales' && (
        <div className="table-section">
          {loading ? (
            <div className="loading">Loading sales...</div>
          ) : (
            <Table
            data={sales}
            columns={[
              {
                key: 'sale_number',
                header: 'Transaction Number',
                render: (value, row) => (
                  <div className="transaction-info">
        <span className={`transaction-number ${row.type}`}>
          {row.sale_number}
        </span>
        <span className={`transaction-type ${row.type}`}>
          {row.type === 'return' ? 'RETURN' : 'SALE'}
        </span>
        {row.type === 'return' && (
          <span className="original-sale">
            From: {row.original_sale_number}
          </span>
        )}
                  </div>
                )
              },
              {
                key: 'customer_name',
                header: 'Customer',
                render: (value) => value || 'N/A'
              },
              {
                key: 'status',
                header: 'Status',
                render: (value, row) => {
                  if (row.type === 'return') {
                    const returnStatusClasses = {
                      pending: 'status-pending',
                      completed: 'status-completed',
                      cancelled: 'status-cancelled'
                    };
                    return <span className={`status-badge ${returnStatusClasses[value] || ''}`}>{value}</span>;
                  }
                  return getStatusBadge(value);
                }
              },
              {
                key: 'items',
                header: 'Items',
                render: (items, row) => {
                  if (!items || items.length === 0) {
                    return <span className="no-items">No items</span>;
                  }
                  return (
                    <div className={`sale-items ${row.type}`}>
                      {items.slice(0, 2).map((item, index) => (
                        <div key={index} className="sale-item-row">
                          <span className="item-name">
                            {item.product_name}
                            <span className={`price-mode-badge ${item.price_mode || 'standard'}`}>
                              {item.price_mode === 'wholesale' ? 'WS' : 'STD'}
                            </span>
                            {row.type === 'return' && (
                              <span className="return-badge">RETURN</span>
                            )}
                          </span>
                          <span className="item-details">
                            {item.quantity_display || item.quantity} {item.unit_symbol || 'pcs'} × {formatCurrency(item.unit_price)}
                          </span>
                        </div>
                      ))}
                      {items.length > 2 && (
                        <div className="more-items">+{items.length - 2} more items</div>
                      )}
                    </div>
                  );
                }
              },
              {
                key: 'total_amount',
                header: 'Total Amount',
                render: (value) => formatCurrency(value)
              },
              {
                key: 'created_at',
                header: 'Date',
                render: (value) => formatDate(value)
              },
              {
                key: 'sold_by_name',
                header: 'Sold By',
                render: (value) => value || 'N/A'
              },
              {
                key: 'actions',
                header: 'Actions',
                render: (_, row) => (
                  <div className="action-buttons">
                    <PrintButton
                      data={{
                        ...row,
                        user_name: user?.username || 'Unknown User',
                        user_id: user?.id || 'unknown',
                        print_timestamp: new Date().toISOString(),
                        print_id: `PRINT-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
                      }}
                      title={row.type === 'return' ? "Return Receipt" : "Sale Receipt"}
                      type={row.type === 'return' ? "return" : "sale"}
                      printText="Print"
                      className="print-sale-btn"
                    />
                    {row.type === 'sale' && (
                      <>
                        <Button 
                          variant="primary" 
                          size="small"
                          onClick={() => openEditModal(row)}
                          loading={loadingSale}
                          disabled={loadingSale}
                        >
                          Edit
                        </Button>
                        {row.status === 'completed' && (
                          <Button 
                            variant="warning" 
                            size="small"
                            onClick={() => openReturnModal(row)}
                            loading={loadingReturnData}
                            disabled={loadingReturnData}
                          >
                            Return
                          </Button>
                        )}
                      </>
                    )}
                    {row.type === 'return' && row.status === 'pending' && (
                      <Button 
                        variant="success" 
                        size="small"
                        onClick={() => completeReturn(row.id)}
                      >
                        Complete
                      </Button>
                    )}
                  </div>
                )
              }
            ]}
          />
        )}
        </div>
      )}

      {/* Delete Sales Modal */}
      {showDeleteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Delete Sales</h2>
              <button className="close-button" onClick={() => setShowDeleteModal(false)}>&times;</button>
            </div>
            <div className="modal-body">
              <p>Select criteria to delete sales:</p>

              <div className="form-group">
                <label>Customer Name (optional)</label>
                <input
                  type="text"
                  name="customer_name"
                  value={deleteFilters.customer_name}
                  onChange={handleDeleteFilterChange}
                  placeholder="Leave empty to delete all"
                />
              </div>

              <div className="form-group">
                <label>Start Date (optional)</label>
                <input
                  type="date"
                  name="start_date"
                  value={deleteFilters.start_date}
                  onChange={handleDeleteFilterChange}
                />
              </div>

              <div className="form-group">
                <label>End Date (optional)</label>
                <input
                  type="date"
                  name="end_date"
                  value={deleteFilters.end_date}
                  onChange={handleDeleteFilterChange}
                />
              </div>

              <div className="form-group">
                <label>Status</label>
                <select
                  name="status"
                  value={deleteFilters.status}
                  onChange={handleDeleteFilterChange}
                >
                  <option value="completed">Completed</option>
                  <option value="pending">Pending</option>
                  <option value="cancelled">Cancelled</option>
                  <option value="returned">Returned</option>
                </select>
              </div>

              <div className="modal-actions">
                <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
                  Cancel
                </Button>
                <Button variant="danger" onClick={handleDeleteSales}>
                  Delete Sales
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Edit Sale Modal */}
      {showEditModal && (
        <div className="modal-overlay">
          <div className="modal-content large-modal">
            <div className="modal-header">
              <h2>Edit Sale: {selectedSale?.sale_number || 'Loading...'}</h2>
              <button className="close-button" onClick={() => setShowEditModal(false)}>&times;</button>
            </div>
            <div className="modal-body">
              {loadingSale ? (
                <div className="loading">Loading sale details...</div>
              ) : selectedSale ? (
                <>
                  <div className="edit-sale-info">
                    <p><strong>Customer:</strong> {selectedSale.customer_name || 'N/A'}</p>
                    <p><strong>Date:</strong> {formatDate(selectedSale.created_at)}</p>
                    <p><strong>Status:</strong> {getStatusBadge(selectedSale.status)}</p>
                  </div>

                  <h3>Sale Items</h3>
              <div className="edit-items">
                {editFormData.items.map((item, index) => {
                  // Get the original item from the sale to display product and unit info
                  const originalItem = selectedSale?.items?.find(origItem => 
                    origItem.product === item.product && 
                    origItem.unit === item.unit &&
                    origItem.price_mode === item.price_mode
                  );

                  return (
                    <div key={index} className="edit-item">
                      <div className="form-group">
                        <label>Product</label>
                        <input
                          type="text"
                          value={originalItem?.product_name || 'Unknown Product'}
                          readOnly
                          className="readonly-field"
                        />
                      </div>

                      <div className="form-group">
                        <label>Unit</label>
                        <input
                          type="text"
                          value={originalItem?.unit_name ? `${originalItem.unit_name} (${originalItem.unit_symbol})` : 'N/A'}
                          readOnly
                          className="readonly-field"
                        />
                      </div>

                      <div className="form-group">
                        <label>Price Mode</label>
                        <input
                          type="text"
                          value={item.price_mode === 'wholesale' ? 'Wholesale (WS)' : 'Standard (STD)'}
                          readOnly
                          className="readonly-field"
                        />
                      </div>

                      <div className="form-group">
                        <label>Unit Price</label>
                        <input
                          type="text"
                          value={formatCurrency(item.unit_price)}
                          readOnly
                          className="readonly-field"
                        />
                      </div>

                      <div className="form-group">
                        <label>Quantity</label>
                        <input
                          type="number"
                          min="1"
                          value={item.quantity}
                          onChange={(e) => updateEditItem(index, 'quantity', parseFloat(e.target.value) || 1)}
                        />
                      </div>

                      <div className="form-group">
                        <label>Total</label>
                        <input
                          type="text"
                          value={formatCurrency(item.quantity * item.unit_price)}
                          readOnly
                          className="readonly-field"
                        />
                      </div>

                    <Button 
                      variant="danger" 
                      size="small"
                      onClick={() => removeEditItem(index)}
                    >
                      Remove
                    </Button>
                  </div>
                  );
                })}

                <Button variant="primary" onClick={addEditItem}>
                  Add Item
                </Button>
              </div>

                  <div className="modal-actions">
                    <Button variant="secondary" onClick={() => setShowEditModal(false)}>
                      Cancel
                    </Button>
                    <Button 
                      variant="primary" 
                      onClick={handleEditSale}
                      disabled={!validateEditSale()}
                    >
                      Update Sale
                    </Button>
                  </div>
                </>
              ) : (
                <div className="error-message">
                  Failed to load sale details
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Return Modal */}
      <ReturnModal
        isOpen={showReturnModal}
        onClose={() => {
          setShowReturnModal(false);
          setSelectedSaleForReturn(null);
          setSaleItemsForReturn([]);
        }}
        onSave={handleCreateReturn}
        sale={selectedSaleForReturn}
        saleItems={saleItemsForReturn}
      />
    </div>
  );
};

export default SalesManagement;
