import React, { useState, useEffect } from 'react';
import api from '../services/api';
import Button from '../components/Button';
import { formatCurrency, formatDate } from '../utils/helpers';
import { useAuth } from '../contexts/AuthContext';
import './PendingSales.css';

const PendingSales = () => {
  const { user, loading: authLoading } = useAuth();
  const [pendingSales, setPendingSales] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showSaleModal, setShowSaleModal] = useState(false);
  const [selectedSale, setSelectedSale] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingSale, setEditingSale] = useState(null);
  const [editFormData, setEditFormData] = useState({});
  const [completingSale, setCompletingSale] = useState(null);
  const [stockValidationStatus, setStockValidationStatus] = useState({});

  useEffect(() => {
    if (!authLoading && user) {
      fetchPendingSales();
    }
  }, [authLoading, user]);

  useEffect(() => {
    if (pendingSales.length > 0) {
      validateStockForAllSales();
    }
  }, [pendingSales]);

  const fetchPendingSales = async () => {
    try {
      setLoading(true);
      console.log('Fetching pending sales from:', '/sales/pending/');

      // Check if user is authenticated
      const token = localStorage.getItem('accessToken');
      console.log('Auth token exists:', !!token);
      console.log('Auth token:', token ? token.substring(0, 20) + '...' : 'None');

      const response = await api.get('/sales/pending/');
      console.log('Pending sales response:', response.data);
      setPendingSales(response.data);
    } catch (error) {
      console.error('Error fetching pending sales:', error);
      console.error('Error details:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
        statusText: error.response?.statusText
      });

      // More specific error handling
      if (error.response?.status === 401) {
        alert('Authentication required. Please log in again.');
        // Redirect to login or refresh token
        window.location.href = '/login';
      } else {
        alert('Error fetching pending sales: ' + (error.response?.data?.detail || error.message));
      }
    } finally {
      setLoading(false);
    }
  };

  const validateStockForAllSales = async () => {
    const validationStatus = {};

    for (const sale of pendingSales) {
      let hasInsufficientStock = false;

      for (const item of sale.items || []) {
        try {
          const response = await api.get(`/products/${item.product}/stock-availability/`);
          const stockData = response.data;

          if (stockData.available_units) {
            const unitData = stockData.available_units.find(unit => unit.id === item.unit);
            if (unitData && unitData.available_quantity < item.quantity_display) {
              hasInsufficientStock = true;
              break;
            }
          }
        } catch (error) {
          console.error(`Error checking stock for product ${item.product}:`, error);
        }
      }

      validationStatus[sale.id] = { hasInsufficientStock };
    }

    setStockValidationStatus(validationStatus);
  };

  const completeSale = async (saleId) => {
    try {
      setCompletingSale(saleId);

      // Check stock before completing
      const sale = pendingSales.find(s => s.id === saleId);
      const stockValidationErrors = [];

      for (const item of sale.items || []) {
        try {
          const response = await api.get(`/products/${item.product}/stock-availability/`);
          const stockData = response.data;

          if (stockData.available_units) {
            const unitData = stockData.available_units.find(unit => unit.id === item.unit);
            if (unitData && unitData.available_quantity < item.quantity_display) {
              stockValidationErrors.push(`${item.product_name}: Required ${item.quantity_display} ${item.unit_name}, but only ${unitData.available_quantity} available`);
            }
          }
        } catch (error) {
          console.error(`Error checking stock for product ${item.product}:`, error);
          stockValidationErrors.push(`${item.product_name}: Unable to check stock availability`);
        }
      }

      if (stockValidationErrors.length > 0) {
        alert('Cannot complete sale due to insufficient stock:\n' + stockValidationErrors.join('\n'));
        return;
      }

      await api.post(`/sales/${saleId}/complete/`);

      // Print receipt after successful completion
      const completedSale = { ...sale, status: 'completed' };
      printPendingSale(completedSale);

      // Remove from pending sales list
      setPendingSales(prev => prev.filter(sale => sale.id !== saleId));
      setShowSaleModal(false);
      setSelectedSale(null);

      alert('Sale completed successfully!');
    } catch (error) {
      console.error('Error completing sale:', error);
      alert('Error completing sale: ' + (error.response?.data?.detail || error.message));
    } finally {
      setCompletingSale(null);
    }
  };

  const cancelSale = async (saleId) => {
    if (window.confirm('Are you sure you want to cancel this sale?')) {
      try {
        await api.delete(`/sales/${saleId}/`);
        setPendingSales(prev => prev.filter(sale => sale.id !== saleId));
        setShowSaleModal(false);
        setSelectedSale(null);
        alert('Sale cancelled successfully!');
      } catch (error) {
        console.error('Error cancelling sale:', error);
        alert('Error cancelling sale: ' + (error.response?.data?.detail || error.message));
      }
    }
  };

  const printPendingSale = (sale) => {
    const printData = {
      ...sale,
      status: sale.status === 'completed' ? 'completed' : 'pending',
      statusText: sale.status === 'completed' ? 'This sale has been completed' : 'This sale is pending'
    };

    // Create a new window for printing
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>Receipt - ${sale.sale_number}</title>
          <style>
            body { font-family: Arial, sans-serif; margin: 20px; }
            .header { text-align: center; margin-bottom: 20px; }
            .receipt-info { margin-bottom: 20px; }
            .items { margin-bottom: 20px; }
            .item { display: flex; justify-content: space-between; margin-bottom: 5px; }
            .totals { border-top: 1px solid #000; padding-top: 10px; }
            .total-row { display: flex; justify-content: space-between; margin-bottom: 5px; }
            .total-final { font-weight: bold; font-size: 1.2em; }
            .status { text-align: center; margin-top: 20px; font-weight: bold; }
          </style>
        </head>
        <body>
          <div class="header">
            <h1>Beverage Management System</h1>
            <h2>Receipt - ${sale.sale_number}</h2>
          </div>

          <div class="receipt-info">
            <p><strong>Date:</strong> ${formatDate(sale.created_at)}</p>
            <p><strong>Customer:</strong> ${sale.customer_name || 'Walk-in Customer'}</p>
            <p><strong>Payment Method:</strong> ${sale.payment_method?.replace('_', ' ').toUpperCase()}</p>
          </div>

          <div class="items">
            <h3>Items:</h3>
            ${sale.items?.map(item => `
              <div class="item">
                <span>${item.product_name} - ${item.quantity_display || item.quantity} ${item.unit_name} Ã— ${formatCurrency(item.unit_price)} MGA</span>
                <span>${formatCurrency(item.total_price)} MGA</span>
              </div>
            `).join('')}
          </div>

          <div class="totals">
            <div class="total-row">
              <span>Subtotal:</span>
              <span>${formatCurrency(sale.subtotal)} MGA</span>
            </div>
            ${sale.discount_amount > 0 ? `
              <div class="total-row">
                <span>Discount:</span>
                <span>-${formatCurrency(sale.discount_amount)} MGA</span>
              </div>
            ` : ''}
            ${sale.tax_amount > 0 ? `
              <div class="total-row">
                <span>Tax:</span>
                <span>${formatCurrency(sale.tax_amount)} MGA</span>
              </div>
            ` : ''}
            <div class="total-row total-final">
              <span>Total:</span>
              <span>${formatCurrency(sale.total_amount)} MGA</span>
            </div>
          </div>

          <div class="status">
            <p>${printData.statusText}</p>
          </div>
        </body>
      </html>
    `);
    printWindow.document.close();
    printWindow.print();
  };

  const editSale = (sale) => {
    setEditingSale(sale);

    // Initialize items with proper quantities and calculate totals
    const items = sale.items?.map(item => ({
      ...item,
      quantity: parseFloat(item.quantity_display || item.quantity) || 0,
      unit_price: parseFloat(item.unit_price) || 0,
      total_price: (parseFloat(item.quantity_display || item.quantity) || 0) * (parseFloat(item.unit_price) || 0)
    })) || [];

    // Calculate initial totals
    const calculatedSubtotal = items.reduce((sum, item) => sum + item.total_price, 0);

    // If no tax_amount is provided, assume 0% tax (common for many sales)
    const taxAmount = sale.tax_amount || 0;
    const discountAmount = sale.discount_amount || 0;
    const calculatedTotal = calculatedSubtotal + taxAmount - discountAmount;

    // Use the sale's total_amount if it exists and is greater than 0, otherwise use calculated total
    const subtotal = sale.subtotal && sale.subtotal > 0 ? sale.subtotal : calculatedSubtotal;
    const total_amount = sale.total_amount && sale.total_amount > 0 ? sale.total_amount : calculatedTotal;

    console.log('Edit Sale Debug:', {
      sale: sale,
      items: items,
      calculatedSubtotal: calculatedSubtotal,
      calculatedTotal: calculatedTotal,
      saleSubtotal: sale.subtotal,
      saleTotalAmount: sale.total_amount,
      finalSubtotal: subtotal,
      finalTotalAmount: total_amount,
      tax_amount: sale.tax_amount,
      discount_amount: sale.discount_amount
    });

    const formData = {
      customer_name: sale.customer_name || '',
      customer_phone: sale.customer_phone || '',
      customer_email: sale.customer_email || '',
      payment_method: sale.payment_method || 'cash',
      items: items,
      subtotal: subtotal,
      total_amount: total_amount,
      tax_amount: sale.tax_amount || 0,
      discount_amount: sale.discount_amount || 0
    };

    console.log('Setting EditFormData:', formData);

    setEditFormData(formData);
    setShowEditModal(true);
    setShowSaleModal(false);
  };

  const handleEditItemQuantity = (itemIndex, newQuantity) => {
    const updatedItems = [...editFormData.items];
    updatedItems[itemIndex].quantity = parseFloat(newQuantity) || 0;
    updatedItems[itemIndex].total_price = updatedItems[itemIndex].quantity * parseFloat(updatedItems[itemIndex].unit_price) || 0;

    const subtotal = updatedItems.reduce((sum, item) => sum + item.total_price, 0);

    console.log('Quantity Change Debug:', {
      itemIndex: itemIndex,
      newQuantity: newQuantity,
      unitPrice: updatedItems[itemIndex].unit_price,
      totalPrice: updatedItems[itemIndex].total_price,
      subtotal: subtotal
    });

    // Fallback to original sale values if editFormData doesn't have them
    const taxAmount = editFormData.tax_amount !== undefined ? editFormData.tax_amount : (editingSale?.tax_amount || 0);
    const discountAmount = editFormData.discount_amount !== undefined ? editFormData.discount_amount : (editingSale?.discount_amount || 0);
    const newTotalAmount = subtotal + taxAmount - discountAmount;

    console.log('Quantity Change Debug - Totals:', {
      subtotal: subtotal,
      taxAmount: taxAmount,
      discountAmount: discountAmount,
      newTotalAmount: newTotalAmount,
      editFormDataTax: editFormData.tax_amount,
      editFormDataDiscount: editFormData.discount_amount
    });

    setEditFormData({
      ...editFormData,
      items: updatedItems,
      subtotal: subtotal,
      total_amount: newTotalAmount
    });
  };

  const removeEditItem = (itemIndex) => {
    const updatedItems = editFormData.items.filter((_, index) => index !== itemIndex);
    const subtotal = updatedItems.reduce((sum, item) => sum + item.total_price, 0);

    // Fallback to original sale values if editFormData doesn't have them
    const taxAmount = editFormData.tax_amount !== undefined ? editFormData.tax_amount : (editingSale?.tax_amount || 0);
    const discountAmount = editFormData.discount_amount !== undefined ? editFormData.discount_amount : (editingSale?.discount_amount || 0);
    const newTotalAmount = subtotal + taxAmount - discountAmount;

    setEditFormData({
      ...editFormData,
      items: updatedItems,
      subtotal: subtotal,
      total_amount: newTotalAmount
    });
  };

  const handleEditItemPriceMode = async (itemIndex, newPriceMode) => {
    try {
      const item = editFormData.items[itemIndex];
      const response = await api.get(`/products/${item.product}/`);
      const product = response.data;

      let newUnitPrice;
      if (newPriceMode === 'wholesale') {
        newUnitPrice = product.wholesale_price || product.price;
      } else {
        newUnitPrice = product.price;
      }

      const updatedItems = [...editFormData.items];
      updatedItems[itemIndex].price_mode = newPriceMode;
      updatedItems[itemIndex].unit_price = parseFloat(newUnitPrice) || 0;
      updatedItems[itemIndex].total_price = updatedItems[itemIndex].quantity * (parseFloat(newUnitPrice) || 0);

      const subtotal = updatedItems.reduce((sum, item) => sum + item.total_price, 0);

      // Fallback to original sale values if editFormData doesn't have them
      const taxAmount = editFormData.tax_amount !== undefined ? editFormData.tax_amount : (editingSale?.tax_amount || 0);
      const discountAmount = editFormData.discount_amount !== undefined ? editFormData.discount_amount : (editingSale?.discount_amount || 0);
      const newTotalAmount = subtotal + taxAmount - discountAmount;

      setEditFormData({
        ...editFormData,
        items: updatedItems,
        subtotal: subtotal,
        total_amount: newTotalAmount
      });
    } catch (error) {
      console.error('Error updating price mode:', error);
      alert('Error updating price mode: ' + (error.response?.data?.detail || error.message));
    }
  };

  const saveEditedSale = async () => {
    try {
      const updateData = {
        customer_name: editFormData.customer_name,
        customer_phone: editFormData.customer_phone,
        customer_email: editFormData.customer_email,
        payment_method: editFormData.payment_method,
        items: editFormData.items.map(item => ({
          id: item.id,
          quantity: item.quantity,
          unit_price: item.unit_price,
          price_mode: item.price_mode
        }))
      };

      console.log('Saving edited sale:', {
        saleId: editingSale.id,
        updateData: updateData,
        editFormData: editFormData
      });

      const response = await api.patch(`/sales/${editingSale.id}/`, updateData);
      console.log('Save response:', response.data);

      setShowEditModal(false);
      setEditingSale(null);
      setEditFormData({});

      // Refresh the pending sales list
      fetchPendingSales();

      alert('Sale updated successfully!');
    } catch (error) {
      console.error('Error updating sale:', error);
      alert('Error updating sale: ' + (error.response?.data?.detail || error.message));
    }
  };

  if (authLoading || loading) {
    return (
      <div className="pending-sales-page">
        <div className="page-header">
          <h1>Pending Sales</h1>
        </div>
        <div className="loading-message">Loading pending sales...</div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="pending-sales-page">
        <div className="page-header">
          <h1>Pending Sales</h1>
        </div>
        <div className="loading-message">Please log in to view pending sales.</div>
      </div>
    );
  }

  return (
    <div className="pending-sales-page">
      <div className="page-header">
        <h1>Pending Sales</h1>
        <p>Manage and validate pending sales before completion</p>
      </div>

      {pendingSales.length === 0 ? (
        <div className="no-pending-sales">
          <div className="no-sales-icon">ðŸ“‹</div>
          <h3>No Pending Sales</h3>
          <p>All sales have been completed or there are no pending sales at the moment.</p>
        </div>
      ) : (
        <div className="pending-sales-grid">
          {pendingSales.map((sale) => (
            <div key={sale.id} className="pending-sale-card">
              <div className="sale-header">
                <div className="sale-number">{sale.sale_number}</div>
                <div className="sale-date">{formatDate(sale.created_at)}</div>
              </div>

              <div className="sale-customer">
                <strong>{sale.customer_name || 'Walk-in Customer'}</strong>
              </div>

              <div className="sale-items">
                <div className="items-count">{sale.items?.length || 0} items</div>
                <div className="items-preview">
                  {sale.items?.slice(0, 3).map((item, index) => (
                    <div key={index} className="item-preview">
                      <span className="item-name">{item.product_name}</span>
                      <span className="item-quantity">{item.quantity_display || item.quantity} {item.unit_name}</span>
                      <span className={`price-mode-badge ${item.price_mode === 'wholesale' ? 'wholesale' : ''}`}>
                        {item.price_mode === 'wholesale' ? 'WS' : 'STD'}
                      </span>
                    </div>
                  ))}
                  {sale.items?.length > 3 && (
                    <div className="more-items">+{sale.items.length - 3} more items</div>
                  )}
                </div>
              </div>

              <div className="sale-total">
                <strong>{formatCurrency(sale.total_amount)} MGA</strong>
              </div>

              <div className="stock-status">
                {stockValidationStatus[sale.id]?.hasInsufficientStock ? (
                  <div className="stock-insufficient">âš ï¸ Insufficient Stock</div>
                ) : (
                  <div className="stock-sufficient">âœ… Stock Available</div>
                )}
              </div>

              <div className="sale-actions">
                <Button
                  size="small"
                  variant="outline"
                  onClick={() => {
                    setSelectedSale(sale);
                    setShowSaleModal(true);
                  }}
                >
                  View Details
                </Button>
                <Button
                  size="small"
                  variant="outline"
                  onClick={() => printPendingSale(sale)}
                >
                  ðŸ–¨ï¸ Print
                </Button>
                <Button
                  size="small"
                  variant="outline"
                  onClick={() => editSale(sale)}
                >
                  âœï¸ Edit
                </Button>
                <Button
                  size="small"
                  variant="primary"
                  disabled={stockValidationStatus[sale.id]?.hasInsufficientStock}
                  onClick={() => completeSale(sale.id)}
                  loading={completingSale === sale.id}
                >
                  {stockValidationStatus[sale.id]?.hasInsufficientStock ? 'Insufficient Stock' : 'Complete Sale'}
                </Button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Sale Details Modal */}
      {showSaleModal && selectedSale && (
        <div className="modal-overlay">
          <div className="modal sale-details-modal">
            <div className="modal-header">
              <h2>Sale Details - {selectedSale.sale_number}</h2>
              <button 
                className="modal-close" 
                onClick={() => {
                  setShowSaleModal(false);
                  setSelectedSale(null);
                }}
              >
                Ã—
              </button>
            </div>

            <div className="modal-body">
              {/* Customer Information */}
              <div className="info-section">
                <h3>Customer Information</h3>
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-label">Name:</span>
                    <span className="info-value">{selectedSale.customer_name || 'Walk-in Customer'}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Phone:</span>
                    <span className="info-value">{selectedSale.customer_phone || 'N/A'}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Email:</span>
                    <span className="info-value">{selectedSale.customer_email || 'N/A'}</span>
                  </div>
                </div>
              </div>

              {/* Sale Information */}
              <div className="info-section">
                <h3>Sale Information</h3>
                <div className="info-grid">
                  <div className="info-item">
                    <span className="info-label">Date:</span>
                    <span className="info-value">{formatDate(selectedSale.created_at)}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Payment Method:</span>
                    <span className="info-value">{selectedSale.payment_method?.replace('_', ' ').toUpperCase()}</span>
                  </div>
                  <div className="info-item">
                    <span className="info-label">Status:</span>
                    <span className="status-badge pending">Pending</span>
                  </div>
                </div>
              </div>

              {/* Items List */}
              <div className="items-section">
                <h3>Items ({selectedSale.items?.length || 0})</h3>
                <div className="items-container">
                  {selectedSale.items?.map((item, index) => (
                    <div key={index} className="item-card">
                      <div className="item-header">
                        <div className="item-name">{item.product_name}</div>
                        <span className={`price-mode-badge ${item.price_mode === 'wholesale' ? 'wholesale' : ''}`}>
                          {item.price_mode === 'wholesale' ? 'WS' : 'STD'}
                        </span>
                      </div>
                      <div className="item-details">
                        <div className="item-quantity">
                          <span className="quantity">{item.quantity_display || item.quantity}</span>
                          <span className="unit">{item.unit_name}</span>
                        </div>
                        <div className="item-price">
                          <span className="unit-price">{formatCurrency(item.unit_price)} MGA each</span>
                          <span className="total-price">{formatCurrency(item.total_price)} MGA</span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Totals */}
              <div className="totals-section">
                <h3>Totals</h3>
                <div className="totals-grid">
                  <div className="total-item">
                    <span className="total-label">Subtotal:</span>
                    <span className="total-value">{formatCurrency(selectedSale.subtotal)} MGA</span>
                  </div>
                  {selectedSale.discount_amount > 0 && (
                    <div className="total-item">
                      <span className="total-label">Discount:</span>
                      <span className="total-value">-{formatCurrency(selectedSale.discount_amount)} MGA</span>
                    </div>
                  )}
                  {selectedSale.tax_amount > 0 && (
                    <div className="total-item">
                      <span className="total-label">Tax:</span>
                      <span className="total-value">{formatCurrency(selectedSale.tax_amount)} MGA</span>
                    </div>
                  )}
                  <div className="total-item total-final">
                    <span className="total-label">Total:</span>
                    <span className="total-value">{formatCurrency(selectedSale.total_amount)} MGA</span>
                  </div>
                </div>
              </div>

              {/* Stock Status */}
              <div className="stock-section">
                <h3>Stock Status</h3>
                <div className="stock-status">
                  {stockValidationStatus[selectedSale.id]?.hasInsufficientStock ? (
                    <div className="stock-insufficient">
                      âš ï¸ Insufficient Stock
                    </div>
                  ) : (
                    <div className="stock-sufficient">
                      âœ… Stock Available
                    </div>
                  )}
                </div>
              </div>
            </div>

            <div className="modal-footer">
              <Button
                variant="secondary"
                onClick={() => {
                  setShowSaleModal(false);
                  setSelectedSale(null);
                }}
              >
                Close
              </Button>
              <Button
                variant="secondary"
                onClick={() => printPendingSale(selectedSale)}
              >
                ðŸ–¨ï¸ Print Receipt
              </Button>
              <Button
                variant="secondary"
                onClick={() => editSale(selectedSale)}
              >
                âœï¸ Edit
              </Button>
              <Button
                variant="success"
                disabled={stockValidationStatus[selectedSale.id]?.hasInsufficientStock}
                onClick={() => completeSale(selectedSale.id)}
              >
                {stockValidationStatus[selectedSale.id]?.hasInsufficientStock ? 'Insufficient Stock' : 'Complete Sale'}
              </Button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Sale Modal */}
      {showEditModal && editingSale && (
        <div className="modal-overlay">
          <div className="modal edit-sale-modal">
            <div className="modal-header">
              <h2>Edit Sale - {editingSale.sale_number}</h2>
              <button 
                className="modal-close" 
                onClick={() => {
                  setShowEditModal(false);
                  setEditingSale(null);
                  setEditFormData({});
                }}
              >
                Ã—
              </button>
            </div>

            <div className="modal-body">
              {/* Customer Information */}
              <div className="form-section">
                <h3>Customer Information</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label>Customer Name</label>
                    <input
                      type="text"
                      value={editFormData.customer_name || ''}
                      onChange={(e) => setEditFormData({...editFormData, customer_name: e.target.value})}
                      placeholder="Customer name"
                    />
                  </div>
                  <div className="form-group">
                    <label>Phone</label>
                    <input
                      type="text"
                      value={editFormData.customer_phone || ''}
                      onChange={(e) => setEditFormData({...editFormData, customer_phone: e.target.value})}
                      placeholder="Phone number"
                    />
                  </div>
                  <div className="form-group">
                    <label>Email</label>
                    <input
                      type="email"
                      value={editFormData.customer_email || ''}
                      onChange={(e) => setEditFormData({...editFormData, customer_email: e.target.value})}
                      placeholder="Email address"
                    />
                  </div>
                  <div className="form-group">
                    <label>Payment Method</label>
                    <select
                      value={editFormData.payment_method || 'cash'}
                      onChange={(e) => setEditFormData({...editFormData, payment_method: e.target.value})}
                    >
                      <option value="cash">Cash</option>
                      <option value="card">Card</option>
                      <option value="mobile_money">Mobile Money</option>
                      <option value="bank_transfer">Bank Transfer</option>
                    </select>
                  </div>
                </div>
              </div>

              {/* Items */}
              <div className="form-section">
                <h3>Items ({editFormData.items?.length || 0})</h3>
                <div className="items-edit-container">
                  {editFormData.items?.map((item, index) => (
                    <div key={index} className="edit-item-card">
                      <div className="item-header">
                        <div className="item-name">{item.product_name}</div>
                        <span className={`price-mode-badge ${item.price_mode === 'wholesale' ? 'wholesale' : ''}`}>
                          {item.price_mode === 'wholesale' ? 'WS' : 'STD'}
                        </span>
                      </div>
                      <div className="item-details">
                        <div className="item-unit">{item.unit_name}</div>
                        <div className="item-price">{formatCurrency(item.unit_price)} MGA each</div>
                      </div>
                      <div className="item-controls">
                        <div className="control-group">
                          <label>Quantity:</label>
                          <input
                            type="number"
                            min="0"
                            step="0.01"
                            value={item.quantity}
                            onChange={(e) => handleEditItemQuantity(index, e.target.value)}
                          />
                        </div>
                        <div className="control-group">
                          <label>Price Mode:</label>
                          <select
                            value={item.price_mode}
                            disabled
                            style={{ backgroundColor: '#f5f5f5', color: '#666' }}
                          >
                            <option value="standard">Standard</option>
                            <option value="wholesale">Wholesale</option>
                          </select>
                        </div>
                        <div className="item-total">
                          {formatCurrency(item.total_price)} MGA
                        </div>
                        <div className="item-actions">
                          <Button
                            size="small"
                            variant="danger"
                            onClick={() => removeEditItem(index)}
                          >
                            Remove
                          </Button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Totals */}
              <div className="totals-section">
                <h3>Totals</h3>
                <div className="totals-grid">
                  <div className="total-item">
                    <span className="total-label">Subtotal:</span>
                    <span className="total-value">{formatCurrency(editFormData.subtotal || 0)} MGA</span>
                  </div>
                  <div className="total-item total-final">
                    <span className="total-label">Total:</span>
                    <span className="total-value">{formatCurrency(editFormData.total_amount || 0)} MGA</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="modal-footer">
              <Button
                variant="secondary"
                onClick={() => {
                  setShowEditModal(false);
                  setEditingSale(null);
                  setEditFormData({});
                }}
              >
                Cancel
              </Button>
              <Button
                variant="primary"
                onClick={saveEditedSale}
              >
                Save Changes
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PendingSales;
