import React, { useState, useEffect } from 'react';
import { useApi } from '../hooks/useApi';
import Button from '../components/Button';
import Table from '../components/Table';
import PurchaseOrderModal from '../components/PurchaseOrderModal';
import DeliveryModal from '../components/DeliveryModal';
import './PurchaseOrders.css';

const PurchaseOrders = () => {
  const [purchaseOrders, setPurchaseOrders] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showDeliveryModal, setShowDeliveryModal] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);

  const [pendingDeliveries, setPendingDeliveries] = useState([]);
  const api = useApi();

  useEffect(() => {
    fetchData();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchData = async () => {
    try {
      setLoading(true);
      const [ordersResponse, suppliersResponse, deliveriesResponse] = await Promise.all([
        api.get('/purchases/purchase-orders/'),
        api.get('/purchases/suppliers/'),
        api.get('/purchases/deliveries/pending/')
      ]);

      setPurchaseOrders(ordersResponse.data.results || ordersResponse.data);
      setSuppliers(suppliersResponse.data.results || suppliersResponse.data);
      setPendingDeliveries(deliveriesResponse.data.results || deliveriesResponse.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateOrder = async (orderData) => {
    try {
      await api.post('/purchases/purchase-orders/', orderData);
      fetchData();
      setShowCreateModal(false);
    } catch (error) {
      console.error('Error creating purchase order:', error);
      alert('Error creating purchase order: ' + (error.response?.data?.detail || error.message));
    }
  };

  const handleCreateDelivery = async (deliveryData) => {
    try {
      console.log('Creating delivery with data:', JSON.stringify(deliveryData, null, 2));
      const response = await api.post('/purchases/deliveries/', deliveryData);
      console.log('Delivery created successfully:', response.data);
      fetchData();
      setShowDeliveryModal(false);
      setSelectedOrder(null);
    } catch (error) {
      console.error('Error creating delivery:', error);
      console.error('Error response:', error.response?.data);
      console.error('Error status:', error.response?.status);
      alert('Error creating delivery: ' + (error.response?.data?.detail || error.message));
    }
  };

  const handleConfirmDelivery = async (deliveryId) => {
    try {
      await api.post('/purchases/deliveries/confirm/', { delivery_id: deliveryId });
      fetchData();
    } catch (error) {
      console.error('Error confirming delivery:', error);
    }
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      draft: 'status-draft',
      sent: 'status-sent',
      confirmed: 'status-confirmed',
      partially_delivered: 'status-partial',
      delivered: 'status-delivered',
      cancelled: 'status-cancelled'
    };

    return (
      <span className={`status-badge ${statusClasses[status] || 'status-default'}`}>
        {status.replace('_', ' ').toUpperCase()}
      </span>
    );
  };

  const purchaseOrderColumns = [
    {
      key: 'order_number',
      label: 'Order Number',
      render: (value, row) => (
        <div>
          <div className="order-number">{value}</div>
          <div className="supplier-name">{row.supplier?.name}</div>
        </div>
      )
    },
    {
      key: 'status',
      label: 'Status',
      render: (value) => getStatusBadge(value)
    },
    {
      key: 'order_date',
      label: 'Order Date',
      render: (value) => new Date(value).toLocaleDateString()
    },
    {
      key: 'expected_delivery_date',
      label: 'Expected Delivery',
      render: (value) => value ? new Date(value).toLocaleDateString() : '-'
    },
    {
      key: 'total_amount',
      label: 'Total Amount',
      render: (value) => `$${parseFloat(value).toFixed(2)}`
    },
    {
      key: 'actions',
      label: 'Actions',
      render: (value, row) => (
        <div className="action-buttons">
          <Button
            size="small"
            variant="secondary"
            onClick={() => {
              setSelectedOrder(row);
              setShowDeliveryModal(true);
            }}
            disabled={row.status === 'cancelled' || row.status === 'delivered'}
          >
            Create Delivery
          </Button>
        </div>
      )
    }
  ];

  const deliveryColumns = [
    {
      key: 'delivery_number',
      label: 'Delivery Number',
      render: (value, row) => (
        <div>
          <div className="delivery-number">{value}</div>
          <div className="order-number">PO: {row.purchase_order?.order_number}</div>
        </div>
      )
    },
    {
      key: 'status',
      label: 'Status',
      render: (value) => getStatusBadge(value)
    },
    {
      key: 'delivery_date',
      label: 'Delivery Date',
      render: (value) => new Date(value).toLocaleDateString()
    },
    {
      key: 'total_amount',
      label: 'Total Amount',
      render: (value) => `$${parseFloat(value).toFixed(2)}`
    },
    {
      key: 'actions',
      label: 'Actions',
      render: (value, row) => (
        <div className="action-buttons">
          <Button
            size="small"
            variant="primary"
            onClick={() => handleConfirmDelivery(row.id)}
            disabled={row.status === 'completed'}
          >
            Confirm & Update Stock
          </Button>
        </div>
      )
    }
  ];

  if (loading) {
    return (
      <div className="page-loading">
        <div className="spinner"></div>
        <span>Loading purchase orders...</span>
      </div>
    );
  }

  return (
    <div className="purchase-orders">
      <div className="page-header">
        <h1>Purchase Orders & Deliveries</h1>
        <div className="header-actions">
          <Button
            variant="primary"
            onClick={() => setShowCreateModal(true)}
          >
            Create Purchase Order
          </Button>
        </div>
      </div>

      <div className="content-sections">
        <div className="section">
          <h2>Purchase Orders</h2>
          <Table
            data={purchaseOrders}
            columns={purchaseOrderColumns}
            emptyMessage="No purchase orders found"
          />
        </div>

        <div className="section">
          <h2>Pending Deliveries</h2>
          <Table
            data={pendingDeliveries}
            columns={deliveryColumns}
            emptyMessage="No pending deliveries"
          />
        </div>
      </div>

      {showCreateModal && (
        <PurchaseOrderModal
          suppliers={suppliers}
          onClose={() => setShowCreateModal(false)}
          onSubmit={handleCreateOrder}
        />
      )}

      {showDeliveryModal && selectedOrder && (
        <DeliveryModal
          purchaseOrder={selectedOrder}
          onClose={() => {
            setShowDeliveryModal(false);
            setSelectedOrder(null);
          }}
          onSubmit={handleCreateDelivery}
        />
      )}
    </div>
  );
};

export default PurchaseOrders;
