import React, { useState, useEffect } from 'react';
import api from '../services/api';
import Button from '../components/Button';
import './PendingSales.css';

const PendingSales = () => {
  const [pendingSales, setPendingSales] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedSale, setSelectedSale] = useState(null);
  const [showSaleModal, setShowSaleModal] = useState(false);
  const [completingSale, setCompletingSale] = useState(null);
  const [stockValidationStatus, setStockValidationStatus] = useState({});

  useEffect(() => {
    fetchPendingSales();
  }, []);

  // Validate stock for all pending sales when the list changes
  useEffect(() => {
    if (pendingSales.length > 0) {
      validateStockForAllSales();
    }
  }, [pendingSales]);

  const fetchPendingSales = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await api.get('/sales/?status=pending');
      setPendingSales(response.data.results || response.data);
    } catch (err) {
      setError('Failed to fetch pending sales');
      console.error('Pending sales fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const validateStockForAllSales = async () => {
    const validationStatus = {};

    for (const sale of pendingSales) {
      let hasInsufficientStock = false;
      const stockIssues = [];

      for (const item of sale.items || []) {
        try {
          const stockResponse = await api.get(`/products/${item.product}/stock-availability/`);
          const stockData = stockResponse.data;

          const unitStockInfo = stockData.find(unit => unit.unit_id === item.unit);
          if (unitStockInfo) {
            const availableQuantity = parseFloat(unitStockInfo.available_quantity || 0);
            const requiredQuantity = parseFloat(item.quantity || 0);

            if (availableQuantity < requiredQuantity) {
              hasInsufficientStock = true;
              stockIssues.push(`${item.product_name}: ${requiredQuantity} ${item.unit_name} required, ${availableQuantity} available`);
            }
          }
        } catch (err) {
          console.error(`Error validating stock for sale ${sale.id}:`, err);
        }
      }

      validationStatus[sale.id] = {
        hasInsufficientStock,
        stockIssues
      };
    }

    setStockValidationStatus(validationStatus);
  };

  const completeSale = async (saleId) => {
    try {
      setCompletingSale(saleId);
      setError('');

      // Find the sale to validate
      const sale = pendingSales.find(s => s.id === saleId);
      if (!sale) {
        setError('Sale not found');
        return;
      }

      // Check stock availability for each item
      const stockValidationErrors = [];

      for (const item of sale.items || []) {
        try {
          // Get current stock availability for this product
          const stockResponse = await api.get(`/products/${item.product}/stock-availability/`);
          const stockData = stockResponse.data;

          // Find the stock info for the unit used in this sale item
          const unitStockInfo = stockData.available_units.find(unit => unit.id === item.unit);
          if (!unitStockInfo) {
            stockValidationErrors.push(`${item.product_name}: Unit not found in stock data`);
            continue;
          }

          // Check if there's enough stock
          const availableQuantity = parseFloat(unitStockInfo.available_quantity || 0);
          const requiredQuantity = parseFloat(item.quantity || 0);

          if (availableQuantity < requiredQuantity) {
            stockValidationErrors.push(
              `${item.product_name}: Insufficient stock. Required: ${requiredQuantity} ${item.unit_name}, Available: ${availableQuantity} ${item.unit_name}`
            );
          }
        } catch (stockErr) {
          console.error(`Error checking stock for product ${item.product}:`, stockErr);
          stockValidationErrors.push(`${item.product_name}: Unable to check stock availability`);
        }
      }

      // If there are stock validation errors, show them and prevent completion
      if (stockValidationErrors.length > 0) {
        setError(`Cannot complete sale due to insufficient stock:\n\n${stockValidationErrors.join('\n')}`);
        return;
      }

      // If stock validation passes, proceed with completion
      await api.post(`/sales/${saleId}/complete/`);

      // Remove the completed sale from the list
      setPendingSales(prev => prev.filter(sale => sale.id !== saleId));

      // Close modal if it was open for this sale
      if (selectedSale && selectedSale.id === saleId) {
        setShowSaleModal(false);
        setSelectedSale(null);
      }

      alert('Sale completed successfully!');
    } catch (err) {
      setError(`Failed to complete sale: ${err.response?.data?.error || err.message}`);
      console.error('Complete sale error:', err);
    } finally {
      setCompletingSale(null);
    }
  };

  const cancelSale = async (saleId) => {
    if (!window.confirm('Are you sure you want to cancel this sale?')) {
      return;
    }

    try {
      setError('');
      await api.patch(`/sales/${saleId}/`, { status: 'cancelled' });

      // Remove the cancelled sale from the list
      setPendingSales(prev => prev.filter(sale => sale.id !== saleId));

      // Close modal if it was open for this sale
      if (selectedSale && selectedSale.id === saleId) {
        setShowSaleModal(false);
        setSelectedSale(null);
      }

      alert('Sale cancelled successfully!');
    } catch (err) {
      setError(`Failed to cancel sale: ${err.response?.data?.error || err.message}`);
      console.error('Cancel sale error:', err);
    }
  };

  const viewSaleDetails = async (sale) => {
    try {
      setSelectedSale(sale);
      setShowSaleModal(true);
    } catch (err) {
      setError('Failed to load sale details');
      console.error('Sale details error:', err);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const formatCurrency = (amount) => {
    return parseFloat(amount).toFixed(2);
  };

  if (loading) {
    return (
      <div className="pending-sales">
        <div className="loading-container">
          <div className="spinner"></div>
          <span>Loading pending sales...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="pending-sales">
      <div className="page-header">
        <h1>Pending Sales Validation</h1>
        <p>Review and complete pending sales</p>
        <div className="header-actions">
          <Button onClick={fetchPendingSales} variant="outline">
            Refresh
          </Button>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <div className="pending-sales-stats">
        <div className="stat-card">
          <div className="stat-number">{pendingSales.length}</div>
          <div className="stat-label">Pending Sales</div>
        </div>
        <div className="stat-card">
          <div className="stat-number">
            {formatCurrency(pendingSales.reduce((total, sale) => total + parseFloat(sale.total_amount), 0))}
          </div>
          <div className="stat-label">Total Value (MGA)</div>
        </div>
      </div>

      {pendingSales.length === 0 ? (
        <div className="no-pending-sales">
          <div className="no-sales-icon">📋</div>
          <h3>No Pending Sales</h3>
          <p>All sales have been processed. Great job!</p>
        </div>
      ) : (
        <div className="pending-sales-list">
          {pendingSales.map(sale => (
            <div key={sale.id} className="pending-sale-card">
              <div className="sale-header">
                <div className="sale-number">{sale.sale_number}</div>
                <div className="sale-status">
                  <span className="status-badge pending">Pending</span>
                </div>
              </div>

              <div className="sale-info">
                <div className="info-row">
                  <span className="label">Customer:</span>
                  <span className="value">{sale.customer_name || 'Walk-in Customer'}</span>
                </div>
                <div className="info-row">
                  <span className="label">Date:</span>
                  <span className="value">{formatDate(sale.created_at)}</span>
                </div>
                <div className="info-row">
                  <span className="label">Items:</span>
                  <span className="value">{sale.items?.length || 0} items</span>
                </div>
                <div className="info-row">
                  <span className="label">Total:</span>
                  <span className="value total-amount">{formatCurrency(sale.total_amount)} MGA</span>
                </div>
                <div className="info-row">
                  <span className="label">Payment:</span>
                  <span className="value">{sale.payment_method?.replace('_', ' ').toUpperCase()}</span>
                </div>

                {/* Stock Status */}
                {stockValidationStatus[sale.id] && (
                  <div className="info-row stock-status">
                    <span className="label">Stock Status:</span>
                    <span className={`value ${stockValidationStatus[sale.id].hasInsufficientStock ? 'stock-insufficient' : 'stock-sufficient'}`}>
                      {stockValidationStatus[sale.id].hasInsufficientStock ? '⚠️ Insufficient Stock' : '✅ Stock Available'}
                    </span>
                  </div>
                )}
              </div>

              <div className="sale-actions">
                <Button
                  size="small"
                  variant="outline"
                  onClick={() => viewSaleDetails(sale)}
                >
                  View Details
                </Button>
                <Button
                  size="small"
                  variant="primary"
                  onClick={() => completeSale(sale.id)}
                  loading={completingSale === sale.id}
                  disabled={completingSale === sale.id || (stockValidationStatus[sale.id]?.hasInsufficientStock)}
                >
                  {stockValidationStatus[sale.id]?.hasInsufficientStock ? 'Insufficient Stock' : 'Complete Sale'}
                </Button>
                <Button
                  size="small"
                  variant="danger"
                  onClick={() => cancelSale(sale.id)}
                >
                  Cancel Sale
                </Button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Sale Details Modal */}
      {showSaleModal && selectedSale && (
        <div className="modal-overlay">
          <div className="modal sale-details-modal">
            <div className="modal-header">
              <h2>Sale Details - {selectedSale.sale_number}</h2>
              <button 
                className="modal-close" 
                onClick={() => {
                  setShowSaleModal(false);
                  setSelectedSale(null);
                }}
              >
                ×
              </button>
            </div>

            <div className="modal-content">
              <div className="sale-details-info">
                <div className="detail-section">
                  <h3>Customer Information</h3>
                  <div className="detail-row">
                    <span className="label">Name:</span>
                    <span className="value">{selectedSale.customer_name || 'Walk-in Customer'}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Phone:</span>
                    <span className="value">{selectedSale.customer_phone || 'N/A'}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Email:</span>
                    <span className="value">{selectedSale.customer_email || 'N/A'}</span>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>Sale Information</h3>
                  <div className="detail-row">
                    <span className="label">Date:</span>
                    <span className="value">{formatDate(selectedSale.created_at)}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Payment Method:</span>
                    <span className="value">{selectedSale.payment_method?.replace('_', ' ').toUpperCase()}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Status:</span>
                    <span className="status-badge pending">Pending</span>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>Items</h3>
                  <div className="items-list">
                    {selectedSale.items?.map((item, index) => (
                      <div key={index} className="item-row">
                        <div className="item-info">
                          <div className="item-name">{item.product_name}</div>
                          <div className="item-details">
                            {item.quantity} {item.unit_name} × {formatCurrency(item.unit_price)} MGA
                          </div>
                        </div>
                        <div className="item-total">
                          {formatCurrency(item.total_price)} MGA
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="detail-section">
                  <h3>Totals</h3>
                  <div className="totals">
                    <div className="total-row">
                      <span className="label">Subtotal:</span>
                      <span className="value">{formatCurrency(selectedSale.subtotal)} MGA</span>
                    </div>
                    {selectedSale.discount_amount > 0 && (
                      <div className="total-row">
                        <span className="label">Discount:</span>
                        <span className="value">-{formatCurrency(selectedSale.discount_amount)} MGA</span>
                      </div>
                    )}
                    {selectedSale.tax_amount > 0 && (
                      <div className="total-row">
                        <span className="label">Tax:</span>
                        <span className="value">{formatCurrency(selectedSale.tax_amount)} MGA</span>
                      </div>
                    )}
                    <div className="total-row total">
                      <span className="label">Total:</span>
                      <span className="value">{formatCurrency(selectedSale.total_amount)} MGA</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <Button
                variant="outline"
                onClick={() => {
                  setShowSaleModal(false);
                  setSelectedSale(null);
                }}
              >
                Close
              </Button>
              <Button
                variant="danger"
                onClick={() => cancelSale(selectedSale.id)}
              >
                Cancel Sale
              </Button>
              <Button
                variant="primary"
                onClick={() => completeSale(selectedSale.id)}
                loading={completingSale === selectedSale.id}
                disabled={completingSale === selectedSale.id || (stockValidationStatus[selectedSale.id]?.hasInsufficientStock)}
              >
                {stockValidationStatus[selectedSale.id]?.hasInsufficientStock ? 'Insufficient Stock' : 'Complete Sale'}
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PendingSales;
