import React, { useState } from 'react';
import Button from './Button';
import './PrintButton.css';

const PrintButton = ({ 
  data, 
  title, 
  type = 'default', 
  onValidate = null, 
  validateText = 'Validate & Print',
  printText = 'Print',
  className = '',
  disabled = false,
  showValidateOption = false
}) => {
  const [isPrinting, setIsPrinting] = useState(false);
  const [showPrintOptions, setShowPrintOptions] = useState(false);

  const handlePrint = async (validateFirst = false) => {
    if (validateFirst && onValidate) {
      try {
        await onValidate();
      } catch (error) {
        console.error('Validation failed:', error);
        return;
      }
    }

    setIsPrinting(true);

    try {
      // Create print content
      const printContent = generatePrintContent(data, title, type);

      // Open print window
      const printWindow = window.open('', '_blank', 'width=800,height=600');
      printWindow.document.write(printContent);
      printWindow.document.close();

      // Wait for content to load then print
      printWindow.onload = () => {
        printWindow.focus();
        printWindow.print();
        printWindow.close();
      };

    } catch (error) {
      console.error('Print error:', error);
      alert('Failed to print. Please try again.');
    } finally {
      setIsPrinting(false);
      setShowPrintOptions(false);
    }
  };

  const generatePrintContent = (data, title, type) => {
    const currentDate = new Date().toLocaleDateString();
    const currentTime = new Date().toLocaleTimeString();
    const printTimestamp = new Date().toISOString();
    const printId = data.print_id || `PRINT-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    let content = `
      <!DOCTYPE html>
      <html>
      <head>
        <title>${title}</title>
        <style>
          body { 
            font-family: Arial, sans-serif; 
            margin: 20px; 
            color: #333;
            line-height: 1.4;
          }
          .header { 
            text-align: center; 
            border-bottom: 2px solid #333; 
            padding-bottom: 10px; 
            margin-bottom: 20px; 
          }
          .header h1 { 
            margin: 0; 
            color: #2c3e50; 
            font-size: 24px;
          }
          .header .date { 
            color: #666; 
            font-size: 14px; 
            margin-top: 5px;
          }
          .header .print-info { 
            color: #888; 
            font-size: 12px; 
            margin-top: 3px;
            font-family: monospace;
          }
          .info-section { 
            margin-bottom: 20px; 
            padding: 15px; 
            background: #f8f9fa; 
            border-radius: 5px; 
          }
          .info-section h3 { 
            margin: 0 0 10px 0; 
            color: #2c3e50; 
            font-size: 16px;
          }
          .info-row { 
            display: flex; 
            justify-content: space-between; 
            margin-bottom: 5px; 
          }
          .info-label { 
            font-weight: bold; 
            color: #555; 
          }
          .info-value { 
            color: #333; 
          }
          table { 
            width: 100%; 
            border-collapse: collapse; 
            margin-top: 10px; 
          }
          th, td { 
            border: 1px solid #ddd; 
            padding: 8px; 
            text-align: left; 
          }
          th { 
            background-color: #f2f2f2; 
            font-weight: bold; 
            color: #2c3e50;
          }
          .total-row { 
            font-weight: bold; 
            background-color: #e9ecef; 
          }
          .footer { 
            margin-top: 30px; 
            text-align: center; 
            color: #666; 
            font-size: 12px; 
            border-top: 1px solid #ddd; 
            padding-top: 10px; 
          }
          @media print {
            body { margin: 0; }
            .no-print { display: none; }
          }
        </style>
      </head>
      <body>
        <div class="header">
          <h1>${title}</h1>
          <div class="date">Generated on ${currentDate} at ${currentTime}</div>
          <div class="print-info">Print ID: ${printId} | Timestamp: ${printTimestamp}</div>
        </div>
    `;

    // Add type-specific content
    switch (type) {
      case 'inventory':
        content += generateInventoryContent(data);
        break;
      case 'purchase_order':
        content += generatePurchaseOrderContent(data);
        break;
      case 'delivery':
        content += generateDeliveryContent(data);
        break;
      case 'sale':
        content += generateSaleContent(data);
        break;
      case 'sales_history':
        content += generateSalesHistoryContent(data);
        break;
      default:
        content += generateDefaultContent(data);
    }

    content += `
        <div class="footer">
          <p>Generated by Beverage Management System</p>
        </div>
      </body>
      </html>
    `;

    return content;
  };

  const generateInventoryContent = (data) => {
    return `
      <div class="info-section">
        <h3>Inventory Summary</h3>
        <div class="info-row">
          <span class="info-label">Total Products:</span>
          <span class="info-value">${data.length}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Low Stock Items:</span>
          <span class="info-value">${data.filter(item => item.is_low_stock).length}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Out of Stock Items:</span>
          <span class="info-value">${data.filter(item => item.is_out_of_stock).length}</span>
        </div>
      </div>

      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>SKU</th>
            <th>Category</th>
            <th>Stock Quantity</th>
            <th>Unit</th>
            <th>Price</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          ${data.map(item => `
            <tr>
              <td>${item.name}</td>
              <td>${item.sku}</td>
              <td>${item.category_name || 'N/A'}</td>
              <td>${item.stock_quantity}</td>
              <td>${item.unit}</td>
              <td>$${parseFloat(item.price).toFixed(2)}</td>
              <td>
                ${item.is_out_of_stock ? 'Out of Stock' : 
                  item.is_low_stock ? 'Low Stock' : 'In Stock'}
              </td>
            </tr>
          `).join('')}
        </tbody>
      </table>
    `;
  };

  const generatePurchaseOrderContent = (data) => {
    return `
      <div class="info-section">
        <h3>Purchase Order Details</h3>
        <div class="info-row">
          <span class="info-label">Order Number:</span>
          <span class="info-value">${data.order_number}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Supplier:</span>
          <span class="info-value">${data.supplier_name}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Order Date:</span>
          <span class="info-value">${new Date(data.order_date).toLocaleDateString()}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Status:</span>
          <span class="info-value">${data.status}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Total Amount:</span>
          <span class="info-value">$${parseFloat(data.total_amount).toFixed(2)}</span>
        </div>
      </div>

      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>SKU</th>
            <th>Quantity</th>
            <th>Unit</th>
            <th>Unit Cost</th>
            <th>Total Cost</th>
          </tr>
        </thead>
        <tbody>
          ${data.items.map(item => `
            <tr>
              <td>${item.product_name}</td>
              <td>${item.product_sku}</td>
              <td>${item.quantity}</td>
              <td>${item.unit_name}</td>
              <td>$${parseFloat(item.unit_cost).toFixed(2)}</td>
              <td>$${parseFloat(item.total_cost).toFixed(2)}</td>
            </tr>
          `).join('')}
        </tbody>
        <tfoot>
          <tr class="total-row">
            <td colspan="5">Total:</td>
            <td>$${parseFloat(data.total_amount).toFixed(2)}</td>
          </tr>
        </tfoot>
      </table>
    `;
  };

  const generateDeliveryContent = (data) => {
    return `
      <div class="info-section">
        <h3>Delivery Details</h3>
        <div class="info-row">
          <span class="info-label">Delivery Number:</span>
          <span class="info-value">${data.delivery_number}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Purchase Order:</span>
          <span class="info-value">${data.purchase_order_number}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Supplier:</span>
          <span class="info-value">${data.supplier_name}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Delivery Date:</span>
          <span class="info-value">${new Date(data.delivery_date).toLocaleDateString()}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Status:</span>
          <span class="info-value">${data.status}</span>
        </div>
      </div>

      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>SKU</th>
            <th>Ordered Qty</th>
            <th>Delivered Qty</th>
            <th>Unit</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          ${data.items.map(item => `
            <tr>
              <td>${item.product_name}</td>
              <td>${item.product_sku}</td>
              <td>${item.ordered_quantity}</td>
              <td>${item.delivered_quantity}</td>
              <td>${item.unit_name}</td>
              <td>${item.status}</td>
            </tr>
          `).join('')}
        </tbody>
      </table>
    `;
  };

  const generateSaleContent = (data) => {
    return `
      <div class="info-section">
        <h3>Sale Details</h3>
        <div class="info-row">
          <span class="info-label">Sale Number:</span>
          <span class="info-value">${data.sale_number}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Customer:</span>
          <span class="info-value">${data.customer_name || 'Walk-in Customer'}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Sale Date:</span>
          <span class="info-value">${new Date(data.created_at).toLocaleDateString()}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Status:</span>
          <span class="info-value">${data.status}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Total Amount:</span>
          <span class="info-value">$${parseFloat(data.total_amount).toFixed(2)}</span>
        </div>
      </div>

      <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>SKU</th>
            <th>Quantity</th>
            <th>Unit</th>
            <th>Unit Price</th>
            <th>Total Price</th>
          </tr>
        </thead>
        <tbody>
          ${data.items.map(item => `
            <tr>
              <td>${item.product_name}</td>
              <td>${item.product_sku}</td>
              <td>${item.quantity}</td>
              <td>${item.unit_name}</td>
              <td>$${parseFloat(item.unit_price).toFixed(2)}</td>
              <td>$${parseFloat(item.total_price).toFixed(2)}</td>
            </tr>
          `).join('')}
        </tbody>
        <tfoot>
          <tr class="total-row">
            <td colspan="5">Total:</td>
            <td>$${parseFloat(data.total_amount).toFixed(2)}</td>
          </tr>
        </tfoot>
      </table>
    `;
  };

  const generateSalesHistoryContent = (data) => {
    return `
      <div class="info-section">
        <h3>Sales History Summary</h3>
        <div class="info-row">
          <span class="info-label">Total Sales:</span>
          <span class="info-value">${data.length}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Total Revenue:</span>
          <span class="info-value">$${data.reduce((sum, sale) => sum + parseFloat(sale.total_amount), 0).toFixed(2)}</span>
        </div>
        <div class="info-row">
          <span class="info-label">Date Range:</span>
          <span class="info-value">${data.length > 0 ? 
            `${new Date(data[data.length - 1].created_at).toLocaleDateString()} - ${new Date(data[0].created_at).toLocaleDateString()}` : 
            'No data'}</span>
        </div>
      </div>

      <table>
        <thead>
          <tr>
            <th>Sale Number</th>
            <th>Customer</th>
            <th>Date</th>
            <th>Items</th>
            <th>Total Amount</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          ${data.map(sale => `
            <tr>
              <td>${sale.sale_number}</td>
              <td>${sale.customer_name || 'Walk-in'}</td>
              <td>${new Date(sale.created_at).toLocaleDateString()}</td>
              <td>${sale.items_count || 0}</td>
              <td>$${parseFloat(sale.total_amount).toFixed(2)}</td>
              <td>${sale.status}</td>
            </tr>
          `).join('')}
        </tbody>
      </table>
    `;
  };

  const generateDefaultContent = (data) => {
    return `
      <div class="info-section">
        <h3>Data Summary</h3>
        <div class="info-row">
          <span class="info-label">Total Items:</span>
          <span class="info-value">${Array.isArray(data) ? data.length : 1}</span>
        </div>
      </div>

      <pre style="white-space: pre-wrap; font-family: Arial, sans-serif;">
        ${JSON.stringify(data, null, 2)}
      </pre>
    `;
  };

  if (showValidateOption && onValidate) {
    return (
      <div className={`print-button-container ${className}`}>
        <Button
          variant="outline"
          size="small"
          onClick={() => setShowPrintOptions(!showPrintOptions)}
          disabled={disabled}
        >
          {isPrinting ? 'Printing...' : 'Print Options'}
        </Button>

        {showPrintOptions && (
          <div className="print-options">
            <Button
              variant="primary"
              size="small"
              onClick={() => handlePrint(false)}
              disabled={disabled || isPrinting}
            >
              {printText}
            </Button>
            <Button
              variant="success"
              size="small"
              onClick={() => handlePrint(true)}
              disabled={disabled || isPrinting}
            >
              {validateText}
            </Button>
          </div>
        )}
      </div>
    );
  }

  return (
    <Button
      variant="outline"
      size="small"
      onClick={() => handlePrint(false)}
      disabled={disabled || isPrinting}
      className={className}
    >
      {isPrinting ? 'Printing...' : printText}
    </Button>
  );
};

export default PrintButton;
