import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import Table from '../components/Table';
import Button from '../components/Button';
import PrintButton from '../components/PrintButton';
import './SalesManagement.css';

const SalesManagement = () => {
  const { user } = useAuth();
  const [sales, setSales] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedSale, setSelectedSale] = useState(null);
  const [loadingSale, setLoadingSale] = useState(false);
  const [deleteFilters, setDeleteFilters] = useState({
    customer_name: '',
    start_date: '',
    end_date: '',
    status: 'completed'
  });
  const [editFormData, setEditFormData] = useState({
    items: []
  });
  const [products, setProducts] = useState([]);
  const [filters, setFilters] = useState({
    status: '',
    customer_name: '',
    start_date: '',
    end_date: ''
  });

  const fetchSales = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      const params = new URLSearchParams();

      if (filters.status) params.append('status', filters.status);
      if (filters.customer_name) params.append('search', filters.customer_name);
      if (filters.start_date) params.append('created_at__date__gte', filters.start_date);
      if (filters.end_date) params.append('created_at__date__lte', filters.end_date);

      const response = await api.get(`/sales/?${params.toString()}`);
      setSales(response.data.results || response.data);
    } catch (err) {
      setError('Failed to fetch sales: ' + (err.response?.data?.error || err.message));
      console.error('Sales fetch error:', err);
    } finally {
      setLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    fetchSales();
    fetchProducts();
  }, [fetchSales]);

  const fetchProducts = async () => {
    try {
      const response = await api.get('/products/?is_active=true');
      setProducts(response.data.results || response.data);
    } catch (err) {
      console.error('Products fetch error:', err);
    }
  };

  const handleDeleteSales = async () => {
    try {
      setError('');
      const response = await api.delete('/sales/delete/', {
        data: deleteFilters
      });

      alert(`Successfully deleted ${response.data.deleted_count} sales`);
      setShowDeleteModal(false);
      setDeleteFilters({
        customer_name: '',
        start_date: '',
        end_date: '',
        status: 'completed'
      });
      fetchSales();
      fetchProducts(); // Refresh products to update stock quantities
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to delete sales');
      console.error('Delete sales error:', err);
    }
  };

  const handleEditSale = async () => {
    try {
      setError('');
      await api.put(`/sales/${selectedSale.id}/edit/`, editFormData);
      alert('Sale updated successfully');
      setShowEditModal(false);
      setSelectedSale(null);
      setEditFormData({ items: [] });
      fetchSales();
      fetchProducts(); // Refresh products to update stock quantities
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to update sale');
      console.error('Edit sale error:', err);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleDeleteFilterChange = (e) => {
    const { name, value } = e.target;
    setDeleteFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const clearFilters = () => {
    setFilters({
      status: '',
      customer_name: '',
      start_date: '',
      end_date: ''
    });
  };

  const openEditModal = async (sale) => {
    try {
      setLoadingSale(true);
      setError('');

      // Fetch full sale data including items
      const response = await api.get(`/sales/${sale.id}/`);
      const fullSale = response.data;

      setSelectedSale(fullSale);
      setEditFormData({
        items: fullSale.items.map(item => ({
          product: item.product,
          quantity: item.quantity,
          unit: item.unit?.id || item.unit || '',
          unit_price: item.unit_price,
          price_mode: item.price_mode || 'standard'
        }))
      });
      setShowEditModal(true);
    } catch (err) {
      setError('Failed to load sale details');
      console.error('Sale fetch error:', err);
    } finally {
      setLoadingSale(false);
    }
  };

  const addEditItem = () => {
    setEditFormData(prev => ({
      ...prev,
      items: [...prev.items, { product: '', quantity: 1, unit: '', unit_price: 0 }]
    }));
  };

  const removeEditItem = (index) => {
    setEditFormData(prev => ({
      ...prev,
      items: prev.items.filter((_, i) => i !== index)
    }));
  };

  const updateEditItem = (index, field, value) => {
    setEditFormData(prev => ({
      ...prev,
      items: prev.items.map((item, i) => 
        i === index ? { ...item, [field]: value } : item
      )
    }));
  };

  const getProductPrice = (productId) => {
    const product = products.find(p => p.id === parseInt(productId));
    return product ? product.price : 0;
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      pending: 'status-pending',
      completed: 'status-completed',
      cancelled: 'status-cancelled',
      returned: 'status-returned'
    };
    return <span className={`status-badge ${statusClasses[status] || ''}`}>{status}</span>;
  };

  // Check if user has permission to manage sales
  const canManageSales = user?.role === 'admin' || user?.role === 'manager';

  if (!canManageSales) {
    return (
      <div className="sales-management">
        <div className="access-denied">
          <h2>Access Denied</h2>
          <p>You don't have permission to manage sales.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="sales-management">
      <div className="page-header">
        <h1>Sales Management</h1>
        <div className="header-actions">
          <PrintButton
            data={{
              ...sales,
              user_name: user?.username || 'Unknown User',
              user_id: user?.id || 'unknown',
              print_timestamp: new Date().toISOString(),
              print_id: `PRINT-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
            }}
            title="Sales Management Report"
            type="sales_history"
            printText="Print Sales Report"
            className="print-sales-report-btn"
          />
          <Button 
            variant="danger" 
            onClick={() => setShowDeleteModal(true)}
          >
            Delete Sales
          </Button>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError('')}>&times;</button>
        </div>
      )}

      {/* Filters */}
      <div className="filters-section">
        <h3>Filters</h3>
        <div className="filters-grid">
          <div className="form-group">
            <label>Status</label>
            <select
              name="status"
              value={filters.status}
              onChange={handleFilterChange}
            >
              <option value="">All Statuses</option>
              <option value="pending">Pending</option>
              <option value="completed">Completed</option>
              <option value="cancelled">Cancelled</option>
              <option value="returned">Returned</option>
            </select>
          </div>

          <div className="form-group">
            <label>Customer Name</label>
            <input
              type="text"
              name="customer_name"
              value={filters.customer_name}
              onChange={handleFilterChange}
              placeholder="Search by customer name"
            />
          </div>

          <div className="form-group">
            <label>Start Date</label>
            <input
              type="date"
              name="start_date"
              value={filters.start_date}
              onChange={handleFilterChange}
            />
          </div>

          <div className="form-group">
            <label>End Date</label>
            <input
              type="date"
              name="end_date"
              value={filters.end_date}
              onChange={handleFilterChange}
            />
          </div>
        </div>

        <div className="filter-actions">
          <Button variant="secondary" onClick={clearFilters}>
            Clear Filters
          </Button>
        </div>
      </div>

      {/* Sales Table */}
      <div className="table-section">
        {loading ? (
          <div className="loading">Loading sales...</div>
        ) : (
          <Table
            data={sales}
            columns={[
              {
                key: 'sale_number',
                header: 'Sale Number',
                render: (value, row) => (
                  <span className="sale-number">{value}</span>
                )
              },
              {
                key: 'customer_name',
                header: 'Customer',
                render: (value) => value || 'N/A'
              },
              {
                key: 'status',
                header: 'Status',
                render: (value) => getStatusBadge(value)
              },
              {
                key: 'items',
                header: 'Items',
                render: (items, row) => {
                  if (!items || items.length === 0) {
                    return <span className="no-items">No items</span>;
                  }
                  return (
                    <div className="sale-items">
                      {items.slice(0, 2).map((item, index) => (
                        <div key={index} className="sale-item-row">
                          <span className="item-name">
                            {item.product_name}
                            <span className={`price-mode-badge ${item.price_mode || 'standard'}`}>
                              {item.price_mode === 'wholesale' ? 'WS' : 'STD'}
                            </span>
                          </span>
                          <span className="item-details">
                            {item.quantity} {item.unit_symbol || 'pcs'} × {formatCurrency(item.unit_price)}
                          </span>
                        </div>
                      ))}
                      {items.length > 2 && (
                        <div className="more-items">+{items.length - 2} more items</div>
                      )}
                    </div>
                  );
                }
              },
              {
                key: 'total_amount',
                header: 'Total Amount',
                render: (value) => formatCurrency(value)
              },
              {
                key: 'created_at',
                header: 'Date',
                render: (value) => formatDate(value)
              },
              {
                key: 'sold_by_name',
                header: 'Sold By',
                render: (value) => value || 'N/A'
              },
              {
                key: 'actions',
                header: 'Actions',
                render: (_, row) => (
                  <div className="action-buttons">
                    <PrintButton
                      data={{
                        ...row,
                        user_name: user?.username || 'Unknown User',
                        user_id: user?.id || 'unknown',
                        print_timestamp: new Date().toISOString(),
                        print_id: `PRINT-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
                      }}
                      title="Sale Receipt"
                      type="sale"
                      printText="Print"
                      className="print-sale-btn"
                    />
                    <Button 
                      variant="primary" 
                      size="small"
                      onClick={() => openEditModal(row)}
                      loading={loadingSale}
                      disabled={loadingSale}
                    >
                      Edit
                    </Button>
                  </div>
                )
              }
            ]}
          />
        )}
      </div>

      {/* Delete Sales Modal */}
      {showDeleteModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Delete Sales</h2>
              <button className="close-button" onClick={() => setShowDeleteModal(false)}>&times;</button>
            </div>
            <div className="modal-body">
              <p>Select criteria to delete sales:</p>

              <div className="form-group">
                <label>Customer Name (optional)</label>
                <input
                  type="text"
                  name="customer_name"
                  value={deleteFilters.customer_name}
                  onChange={handleDeleteFilterChange}
                  placeholder="Leave empty to delete all"
                />
              </div>

              <div className="form-group">
                <label>Start Date (optional)</label>
                <input
                  type="date"
                  name="start_date"
                  value={deleteFilters.start_date}
                  onChange={handleDeleteFilterChange}
                />
              </div>

              <div className="form-group">
                <label>End Date (optional)</label>
                <input
                  type="date"
                  name="end_date"
                  value={deleteFilters.end_date}
                  onChange={handleDeleteFilterChange}
                />
              </div>

              <div className="form-group">
                <label>Status</label>
                <select
                  name="status"
                  value={deleteFilters.status}
                  onChange={handleDeleteFilterChange}
                >
                  <option value="completed">Completed</option>
                  <option value="pending">Pending</option>
                  <option value="cancelled">Cancelled</option>
                  <option value="returned">Returned</option>
                </select>
              </div>

              <div className="modal-actions">
                <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
                  Cancel
                </Button>
                <Button variant="danger" onClick={handleDeleteSales}>
                  Delete Sales
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Edit Sale Modal */}
      {showEditModal && (
        <div className="modal-overlay">
          <div className="modal-content large-modal">
            <div className="modal-header">
              <h2>Edit Sale: {selectedSale?.sale_number || 'Loading...'}</h2>
              <button className="close-button" onClick={() => setShowEditModal(false)}>&times;</button>
            </div>
            <div className="modal-body">
              {loadingSale ? (
                <div className="loading">Loading sale details...</div>
              ) : selectedSale ? (
                <>
                  <div className="edit-sale-info">
                    <p><strong>Customer:</strong> {selectedSale.customer_name || 'N/A'}</p>
                    <p><strong>Date:</strong> {formatDate(selectedSale.created_at)}</p>
                    <p><strong>Status:</strong> {getStatusBadge(selectedSale.status)}</p>
                  </div>

                  <h3>Sale Items</h3>
              <div className="edit-items">
                {editFormData.items.map((item, index) => (
                  <div key={index} className="edit-item">
                    <div className="form-group">
                      <label>Product</label>
                      <select
                        value={item.product}
                        onChange={(e) => {
                          updateEditItem(index, 'product', e.target.value);
                          // Update unit price based on product and current price mode
                          const productId = e.target.value;
                          const priceMode = item.price_mode || 'standard';
                          const product = products.find(p => p.id === parseInt(productId));
                          if (product) {
                            const newPrice = priceMode === 'wholesale' 
                              ? (product.wholesale_price || product.price)
                              : product.price;
                            updateEditItem(index, 'unit_price', newPrice?.toFixed(2) || 0);
                          }
                        }}
                      >
                        <option value="">Select Product</option>
                        {products.map(product => {
                          const priceMode = item.price_mode || 'standard';
                          const displayPrice = priceMode === 'wholesale' 
                            ? (product.wholesale_price || product.price)
                            : product.price;
                          return (
                            <option key={product.id} value={product.id}>
                              {product.name} - {formatCurrency(displayPrice)}
                            </option>
                          );
                        })}
                      </select>
                    </div>

                    <div className="form-group">
                      <label>Quantity</label>
                      <input
                        type="number"
                        min="1"
                        value={item.quantity}
                        onChange={(e) => updateEditItem(index, 'quantity', parseInt(e.target.value) || 1)}
                      />
                    </div>

                    <div className="form-group">
                      <label>Unit</label>
                      <select
                        value={item.unit}
                        onChange={(e) => {
                          const unitId = e.target.value;
                          updateEditItem(index, 'unit', unitId);

                          // Auto-set unit price based on selected unit's price and price mode
                          if (unitId) {
                            const selectedProduct = products.find(p => p.id === parseInt(item.product));
                            const selectedUnit = selectedProduct?.available_units?.find(u => u.id === parseInt(unitId));
                            if (selectedUnit) {
                              const priceMode = item.price_mode || 'standard';
                              const newPrice = priceMode === 'wholesale' 
                                ? (selectedUnit.wholesale_price || selectedUnit.price)
                                : selectedUnit.price;
                              updateEditItem(index, 'unit_price', newPrice?.toFixed(2) || 0);
                            }
                          }
                        }}
                      >
                        <option value="">Select Unit</option>
                        {(() => {
                          const selectedProduct = products.find(p => p.id === parseInt(item.product));
                          const priceMode = item.price_mode || 'standard';
                          return selectedProduct?.available_units?.map(unit => {
                            const displayPrice = priceMode === 'wholesale' 
                              ? (unit.wholesale_price || unit.price)
                              : unit.price;
                            return (
                              <option key={unit.id} value={unit.id}>
                                {unit.name} ({unit.symbol}) - {displayPrice?.toFixed(2) || 'N/A'} MGA
                              </option>
                            );
                          }) || [];
                        })()}
                      </select>
                    </div>

                    <div className="form-group">
                      <label>Unit Price</label>
                      <input
                        type="number"
                        min="0"
                        step="0.01"
                        value={item.unit_price}
                        onChange={(e) => updateEditItem(index, 'unit_price', parseFloat(e.target.value) || 0)}
                      />
                    </div>

                    <div className="form-group">
                      <label>Price Mode</label>
                      <select
                        value={item.price_mode || 'standard'}
                        onChange={(e) => {
                          updateEditItem(index, 'price_mode', e.target.value);
                          // Update unit price based on selected price mode
                          const selectedProduct = products.find(p => p.id === parseInt(item.product));
                          if (selectedProduct && item.unit) {
                            const selectedUnit = selectedProduct?.available_units?.find(u => u.id === parseInt(item.unit));
                            if (selectedUnit) {
                              const newPrice = e.target.value === 'wholesale' 
                                ? (selectedUnit.wholesale_price || selectedUnit.price)
                                : selectedUnit.price;
                              updateEditItem(index, 'unit_price', newPrice?.toFixed(2) || 0);
                            }
                          }
                        }}
                      >
                        <option value="standard">Standard</option>
                        <option value="wholesale">Wholesale</option>
                      </select>
                    </div>

                    <div className="form-group">
                      <label>Total</label>
                      <input
                        type="text"
                        value={formatCurrency(item.quantity * item.unit_price)}
                        readOnly
                      />
                    </div>

                    <Button 
                      variant="danger" 
                      size="small"
                      onClick={() => removeEditItem(index)}
                    >
                      Remove
                    </Button>
                  </div>
                ))}

                <Button variant="primary" onClick={addEditItem}>
                  Add Item
                </Button>
              </div>

                  <div className="modal-actions">
                    <Button variant="secondary" onClick={() => setShowEditModal(false)}>
                      Cancel
                    </Button>
                    <Button variant="primary" onClick={handleEditSale}>
                      Update Sale
                    </Button>
                  </div>
                </>
              ) : (
                <div className="error-message">
                  Failed to load sale details
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SalesManagement;
