import React, { useState, useEffect } from 'react';
import api from '../services/api';
import Button from '../components/Button';
import './PendingSales.css';

const PendingSales = () => {
  const [pendingSales, setPendingSales] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedSale, setSelectedSale] = useState(null);
  const [showSaleModal, setShowSaleModal] = useState(false);
  const [completingSale, setCompletingSale] = useState(null);
  const [stockValidationStatus, setStockValidationStatus] = useState({});
  const [editingSale, setEditingSale] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editFormData, setEditFormData] = useState({});

  useEffect(() => {
    fetchPendingSales();
  }, []);

  // Validate stock for all pending sales when the list changes
  useEffect(() => {
    if (pendingSales.length > 0) {
      validateStockForAllSales();
    }
  }, [pendingSales]);

  const fetchPendingSales = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await api.get('/sales/?status=pending');
      setPendingSales(response.data.results || response.data);
    } catch (err) {
      setError('Failed to fetch pending sales');
      console.error('Pending sales fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const validateStockForAllSales = async () => {
    const validationStatus = {};

    for (const sale of pendingSales) {
      let hasInsufficientStock = false;
      const stockIssues = [];

      for (const item of sale.items || []) {
        try {
          const stockResponse = await api.get(`/products/${item.product}/stock-availability/`);
          const stockData = stockResponse.data;

          const unitStockInfo = stockData.available_units.find(unit => unit.id === item.unit);
          if (unitStockInfo) {
            const availableQuantity = parseFloat(unitStockInfo.available_quantity || 0);
            const requiredQuantity = parseFloat(item.quantity || 0);

            if (availableQuantity < requiredQuantity) {
              hasInsufficientStock = true;
              stockIssues.push(`${item.product_name}: ${requiredQuantity} ${item.unit_name} required, ${availableQuantity} available`);
            }
          }
        } catch (err) {
          console.error(`Error validating stock for sale ${sale.id}:`, err);
        }
      }

      validationStatus[sale.id] = {
        hasInsufficientStock,
        stockIssues
      };
    }

    setStockValidationStatus(validationStatus);
  };

  const completeSale = async (saleId) => {
    try {
      setCompletingSale(saleId);
      setError('');

      // Find the sale to validate
      const sale = pendingSales.find(s => s.id === saleId);
      if (!sale) {
        setError('Sale not found');
        return;
      }

      // Check stock availability for each item
      const stockValidationErrors = [];

      for (const item of sale.items || []) {
        try {
          // Get current stock availability for this product
          const stockResponse = await api.get(`/products/${item.product}/stock-availability/`);
          const stockData = stockResponse.data;

          // Find the stock info for the unit used in this sale item
          const unitStockInfo = stockData.available_units.find(unit => unit.id === item.unit);
          if (!unitStockInfo) {
            stockValidationErrors.push(`${item.product_name}: Unit not found in stock data`);
            continue;
          }

          // Check if there's enough stock
          const availableQuantity = parseFloat(unitStockInfo.available_quantity || 0);
          const requiredQuantity = parseFloat(item.quantity || 0);

          if (availableQuantity < requiredQuantity) {
            stockValidationErrors.push(
              `${item.product_name}: Insufficient stock. Required: ${requiredQuantity} ${item.unit_name}, Available: ${availableQuantity} ${item.unit_name}`
            );
          }
        } catch (stockErr) {
          console.error(`Error checking stock for product ${item.product}:`, stockErr);
          stockValidationErrors.push(`${item.product_name}: Unable to check stock availability`);
        }
      }

      // If there are stock validation errors, show them and prevent completion
      if (stockValidationErrors.length > 0) {
        setError(`Cannot complete sale due to insufficient stock:\n\n${stockValidationErrors.join('\n')}`);
        return;
      }

      // If stock validation passes, proceed with completion
      await api.post(`/sales/${saleId}/complete/`);

      // Print receipt for the completed sale
      printPendingSale({ ...sale, status: 'completed' });

      // Remove the completed sale from the list
      setPendingSales(prev => prev.filter(sale => sale.id !== saleId));

      // Close modal if it was open for this sale
      if (selectedSale && selectedSale.id === saleId) {
        setShowSaleModal(false);
        setSelectedSale(null);
      }

      alert('Sale completed successfully!');
    } catch (err) {
      setError(`Failed to complete sale: ${err.response?.data?.error || err.message}`);
      console.error('Complete sale error:', err);
    } finally {
      setCompletingSale(null);
    }
  };

  const cancelSale = async (saleId) => {
    if (!window.confirm('Are you sure you want to cancel this sale?')) {
      return;
    }

    try {
      setError('');
      await api.patch(`/sales/${saleId}/`, { status: 'cancelled' });

      // Remove the cancelled sale from the list
      setPendingSales(prev => prev.filter(sale => sale.id !== saleId));

      // Close modal if it was open for this sale
      if (selectedSale && selectedSale.id === saleId) {
        setShowSaleModal(false);
        setSelectedSale(null);
      }

      alert('Sale cancelled successfully!');
    } catch (err) {
      setError(`Failed to cancel sale: ${err.response?.data?.error || err.message}`);
      console.error('Cancel sale error:', err);
    }
  };

  const viewSaleDetails = async (sale) => {
    try {
      setSelectedSale(sale);
      setShowSaleModal(true);
    } catch (err) {
      setError('Failed to load sale details');
      console.error('Sale details error:', err);
    }
  };

  const editSale = (sale) => {
    setEditingSale(sale);
    setEditFormData({
      customer_name: sale.customer_name || '',
      customer_phone: sale.customer_phone || '',
      customer_email: sale.customer_email || '',
      payment_method: sale.payment_method || 'cash',
      items: sale.items?.map(item => ({
        id: item.id,
        product: item.product,
        product_name: item.product_name,
        unit: item.unit,
        unit_name: item.unit_name,
        quantity: item.quantity_display || item.quantity,
        unit_price: item.unit_price,
        total_price: item.total_price
      })) || []
    });
    setShowEditModal(true);
  };

  const handleEditItemQuantity = (itemIndex, newQuantity) => {
    const updatedItems = [...editFormData.items];
    updatedItems[itemIndex] = {
      ...updatedItems[itemIndex],
      quantity: parseFloat(newQuantity) || 0,
      total_price: (parseFloat(newQuantity) || 0) * parseFloat(updatedItems[itemIndex].unit_price)
    };
    setEditFormData({
      ...editFormData,
      items: updatedItems
    });
  };

  const removeEditItem = (itemIndex) => {
    const updatedItems = editFormData.items.filter((_, index) => index !== itemIndex);
    setEditFormData({
      ...editFormData,
      items: updatedItems
    });
  };

  const saveEditedSale = async () => {
    try {
      setError('');

      // Prepare the data for the API
      const updateData = {
        customer_name: editFormData.customer_name,
        customer_phone: editFormData.customer_phone,
        customer_email: editFormData.customer_email,
        payment_method: editFormData.payment_method,
        items: editFormData.items.map(item => ({
          id: item.id,
          quantity: item.quantity,
          unit_price: item.unit_price
        }))
      };

      await api.patch(`/sales/${editingSale.id}/`, updateData);

      // Refresh the pending sales list
      await fetchPendingSales();

      // Close the edit modal
      setShowEditModal(false);
      setEditingSale(null);
      setEditFormData({});

      alert('Sale updated successfully!');
    } catch (err) {
      setError(`Failed to update sale: ${err.response?.data?.error || err.message}`);
      console.error('Update sale error:', err);
    }
  };

  const printPendingSale = (sale) => {
    // Create print data for the pending sale
    const printData = {
      sale_number: sale.sale_number,
      customer_name: sale.customer_name || 'Walk-in Customer',
      customer_phone: sale.customer_phone || '',
      customer_email: sale.customer_email || '',
      user_name: sale.user_name || 'Unknown User',
      user_id: sale.user_id || 'unknown',
      created_at: sale.created_at,
      print_timestamp: new Date().toISOString(),
      print_id: `PRINT-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      status: 'pending',
      total_amount: sale.total_amount,
      items: sale.items.map(item => ({
        product_name: item.product_name,
        product_sku: item.product_sku || '',
        quantity: item.quantity_display || item.quantity,
        unit_name: item.unit_name,
        unit_price: item.unit_price,
        total_price: item.total_price || (parseFloat(item.quantity_display || item.quantity) * parseFloat(item.unit_price))
      }))
    };

    // Create print content
    const printContent = `
      <!DOCTYPE html>
      <html>
      <head>
        <title>Sale Receipt - ${sale.sale_number}</title>
        <style>
          body { font-family: Arial, sans-serif; margin: 20px; }
          .header { text-align: center; border-bottom: 2px solid #000; padding-bottom: 10px; margin-bottom: 20px; }
          .sale-info { margin-bottom: 20px; }
          .items-table { width: 100%; border-collapse: collapse; margin-bottom: 20px; }
          .items-table th, .items-table td { border: 1px solid #000; padding: 8px; text-align: left; }
          .items-table th { background-color: #f0f0f0; }
          .total { text-align: right; font-weight: bold; font-size: 1.2em; }
          .status { text-align: center; margin-top: 20px; padding: 10px; background-color: #fff3cd; border: 1px solid #ffeaa7; }
        </style>
      </head>
      <body>
        <div class="header">
          <h1>Sale Receipt</h1>
          <h2>${printData.sale_number}</h2>
        </div>

        <div class="sale-info">
          <p><strong>Customer:</strong> ${printData.customer_name}</p>
          ${printData.customer_phone ? `<p><strong>Phone:</strong> ${printData.customer_phone}</p>` : ''}
          ${printData.customer_email ? `<p><strong>Email:</strong> ${printData.customer_email}</p>` : ''}
          <p><strong>Date:</strong> ${new Date(printData.created_at).toLocaleString()}</p>
          <p><strong>Cashier:</strong> ${printData.user_name}</p>
        </div>

        <table class="items-table">
          <thead>
            <tr>
              <th>Product</th>
              <th>Unit</th>
              <th>Qty</th>
              <th>Unit Price</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            ${printData.items.map(item => `
              <tr>
                <td>${item.product_name}</td>
                <td>${item.unit_name}</td>
                <td>${item.quantity}</td>
                <td>${parseFloat(item.unit_price).toFixed(2)} MGA</td>
                <td>${parseFloat(item.total_price).toFixed(2)} MGA</td>
              </tr>
            `).join('')}
          </tbody>
        </table>

        <div class="total">
          <p><strong>Total Amount: ${parseFloat(printData.total_amount).toFixed(2)} MGA</strong></p>
        </div>

        <div class="status">
          <p><strong>Status: ${printData.status.toUpperCase()}</strong></p>
          <p>${printData.status === 'pending' ? 'This sale is pending' : 'This sale has been completed'}</p>
        </div>

        <div style="text-align: center; margin-top: 20px; font-size: 0.8em; color: #666;">
          <p>Printed on: ${new Date(printData.print_timestamp).toLocaleString()}</p>
          <p>Print ID: ${printData.print_id}</p>
        </div>
      </body>
      </html>
    `;

    // Open print window
    const printWindow = window.open('', '_blank', 'width=800,height=600');
    printWindow.document.write(printContent);
    printWindow.document.close();

    // Wait for content to load then print
    printWindow.onload = () => {
      printWindow.focus();
      printWindow.print();
      // Close the window after a short delay
      setTimeout(() => {
        printWindow.close();
      }, 1000);
    };
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const formatCurrency = (amount) => {
    return parseFloat(amount).toFixed(2);
  };

  if (loading) {
    return (
      <div className="pending-sales">
        <div className="loading-container">
          <div className="spinner"></div>
          <span>Loading pending sales...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="pending-sales">
      <div className="page-header">
        <h1>Pending Sales Validation</h1>
        <p>Review and complete pending sales</p>
        <div className="header-actions">
          <Button onClick={fetchPendingSales} variant="outline">
            Refresh
          </Button>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <div className="pending-sales-stats">
        <div className="stat-card">
          <div className="stat-number">{pendingSales.length}</div>
          <div className="stat-label">Pending Sales</div>
        </div>
        <div className="stat-card">
          <div className="stat-number">
            {formatCurrency(pendingSales.reduce((total, sale) => total + parseFloat(sale.total_amount), 0))}
          </div>
          <div className="stat-label">Total Value (MGA)</div>
        </div>
      </div>

      {pendingSales.length === 0 ? (
        <div className="no-pending-sales">
          <div className="no-sales-icon">📋</div>
          <h3>No Pending Sales</h3>
          <p>All sales have been processed. Great job!</p>
        </div>
      ) : (
        <div className="pending-sales-list">
          {pendingSales.map(sale => (
            <div key={sale.id} className="pending-sale-card">
              <div className="sale-header">
                <div className="sale-number">{sale.sale_number}</div>
                <div className="sale-status">
                  <span className="status-badge pending">Pending</span>
                </div>
              </div>

              <div className="sale-info">
                <div className="info-row">
                  <span className="label">Customer:</span>
                  <span className="value">{sale.customer_name || 'Walk-in Customer'}</span>
                </div>
                <div className="info-row">
                  <span className="label">Date:</span>
                  <span className="value">{formatDate(sale.created_at)}</span>
                </div>
                <div className="info-row">
                  <span className="label">Items:</span>
                  <span className="value">{sale.items?.length || 0} items</span>
                </div>

                {/* Show item details */}
                {sale.items && sale.items.length > 0 && (
                  <div className="items-preview">
                    {sale.items.slice(0, 3).map((item, index) => (
                      <div key={index} className="item-preview">
                        <span className="item-name">{item.product_name}</span>
                        <span className="item-quantity">{item.quantity_display || item.quantity} {item.unit_name}</span>
                        <span className="item-price">{formatCurrency(item.total_price)} MGA</span>
                      </div>
                    ))}
                    {sale.items.length > 3 && (
                      <div className="more-items">
                        +{sale.items.length - 3} more items
                      </div>
                    )}
                  </div>
                )}
                <div className="info-row">
                  <span className="label">Total:</span>
                  <span className="value total-amount">{formatCurrency(sale.total_amount)} MGA</span>
                </div>
                <div className="info-row">
                  <span className="label">Payment:</span>
                  <span className="value">{sale.payment_method?.replace('_', ' ').toUpperCase()}</span>
                </div>

                {/* Stock Status */}
                {stockValidationStatus[sale.id] && (
                  <div className="info-row stock-status">
                    <span className="label">Stock Status:</span>
                    <span className={`value ${stockValidationStatus[sale.id].hasInsufficientStock ? 'stock-insufficient' : 'stock-sufficient'}`}>
                      {stockValidationStatus[sale.id].hasInsufficientStock ? '⚠️ Insufficient Stock' : '✅ Stock Available'}
                    </span>
                  </div>
                )}
              </div>

              <div className="sale-actions">
                <Button
                  size="small"
                  variant="outline"
                  onClick={() => viewSaleDetails(sale)}
                >
                  View Details
                </Button>
                <Button
                  size="small"
                  variant="outline"
                  onClick={() => editSale(sale)}
                >
                  ✏️ Edit
                </Button>
                <Button
                  size="small"
                  variant="outline"
                  onClick={() => printPendingSale(sale)}
                >
                  🖨️ Print
                </Button>
                <Button
                  size="small"
                  variant="primary"
                  onClick={() => completeSale(sale.id)}
                  loading={completingSale === sale.id}
                  disabled={completingSale === sale.id || (stockValidationStatus[sale.id]?.hasInsufficientStock)}
                >
                  {stockValidationStatus[sale.id]?.hasInsufficientStock ? 'Insufficient Stock' : 'Complete Sale'}
                </Button>
                <Button
                  size="small"
                  variant="danger"
                  onClick={() => cancelSale(sale.id)}
                >
                  Cancel Sale
                </Button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Sale Details Modal */}
      {showSaleModal && selectedSale && (
        <div className="modal-overlay">
          <div className="modal sale-details-modal">
            <div className="modal-header">
              <h2>Sale Details - {selectedSale.sale_number}</h2>
              <button 
                className="modal-close" 
                onClick={() => {
                  setShowSaleModal(false);
                  setSelectedSale(null);
                }}
              >
                ×
              </button>
            </div>

            <div className="modal-content">
              <div className="sale-details-info">
                <div className="detail-section">
                  <h3>Customer Information</h3>
                  <div className="detail-row">
                    <span className="label">Name:</span>
                    <span className="value">{selectedSale.customer_name || 'Walk-in Customer'}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Phone:</span>
                    <span className="value">{selectedSale.customer_phone || 'N/A'}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Email:</span>
                    <span className="value">{selectedSale.customer_email || 'N/A'}</span>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>Sale Information</h3>
                  <div className="detail-row">
                    <span className="label">Date:</span>
                    <span className="value">{formatDate(selectedSale.created_at)}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Payment Method:</span>
                    <span className="value">{selectedSale.payment_method?.replace('_', ' ').toUpperCase()}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Status:</span>
                    <span className="status-badge pending">Pending</span>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>Items</h3>
                  <div className="items-list">
                    {selectedSale.items?.map((item, index) => (
                      <div key={index} className="item-row">
                        <div className="item-info">
                          <div className="item-name">{item.product_name}</div>
                          <div className="item-details">
                            {item.quantity} {item.unit_name} × {formatCurrency(item.unit_price)} MGA
                          </div>
                        </div>
                        <div className="item-total">
                          {formatCurrency(item.total_price)} MGA
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="detail-section">
                  <h3>Totals</h3>
                  <div className="totals">
                    <div className="total-row">
                      <span className="label">Subtotal:</span>
                      <span className="value">{formatCurrency(selectedSale.subtotal)} MGA</span>
                    </div>
                    {selectedSale.discount_amount > 0 && (
                      <div className="total-row">
                        <span className="label">Discount:</span>
                        <span className="value">-{formatCurrency(selectedSale.discount_amount)} MGA</span>
                      </div>
                    )}
                    {selectedSale.tax_amount > 0 && (
                      <div className="total-row">
                        <span className="label">Tax:</span>
                        <span className="value">{formatCurrency(selectedSale.tax_amount)} MGA</span>
                      </div>
                    )}
                    <div className="total-row total">
                      <span className="label">Total:</span>
                      <span className="value">{formatCurrency(selectedSale.total_amount)} MGA</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <Button
                variant="outline"
                onClick={() => {
                  setShowSaleModal(false);
                  setSelectedSale(null);
                }}
              >
                Close
              </Button>
              <Button
                variant="outline"
                onClick={() => {
                  setShowSaleModal(false);
                  setSelectedSale(null);
                  editSale(selectedSale);
                }}
              >
                ✏️ Edit
              </Button>
              <Button
                variant="outline"
                onClick={() => printPendingSale(selectedSale)}
              >
                🖨️ Print Receipt
              </Button>
              <Button
                variant="danger"
                onClick={() => cancelSale(selectedSale.id)}
              >
                Cancel Sale
              </Button>
              <Button
                variant="primary"
                onClick={() => completeSale(selectedSale.id)}
                loading={completingSale === selectedSale.id}
                disabled={completingSale === selectedSale.id || (stockValidationStatus[selectedSale.id]?.hasInsufficientStock)}
              >
                {stockValidationStatus[selectedSale.id]?.hasInsufficientStock ? 'Insufficient Stock' : 'Complete Sale'}
              </Button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Sale Modal */}
      {showEditModal && editingSale && (
        <div className="modal-overlay">
          <div className="modal edit-sale-modal">
            <div className="modal-header">
              <h2>Edit Sale - {editingSale.sale_number}</h2>
              <button 
                className="modal-close" 
                onClick={() => {
                  setShowEditModal(false);
                  setEditingSale(null);
                  setEditFormData({});
                }}
              >
                ×
              </button>
            </div>

            <div className="modal-content">
              <div className="edit-form">
                <div className="form-section">
                  <h3>Customer Information</h3>
                  <div className="form-row">
                    <div className="form-group">
                      <label>Customer Name</label>
                      <input
                        type="text"
                        value={editFormData.customer_name || ''}
                        onChange={(e) => setEditFormData({...editFormData, customer_name: e.target.value})}
                        placeholder="Customer name"
                      />
                    </div>
                    <div className="form-group">
                      <label>Phone</label>
                      <input
                        type="text"
                        value={editFormData.customer_phone || ''}
                        onChange={(e) => setEditFormData({...editFormData, customer_phone: e.target.value})}
                        placeholder="Phone number"
                      />
                    </div>
                  </div>
                  <div className="form-row">
                    <div className="form-group">
                      <label>Email</label>
                      <input
                        type="email"
                        value={editFormData.customer_email || ''}
                        onChange={(e) => setEditFormData({...editFormData, customer_email: e.target.value})}
                        placeholder="Email address"
                      />
                    </div>
                    <div className="form-group">
                      <label>Payment Method</label>
                      <select
                        value={editFormData.payment_method || 'cash'}
                        onChange={(e) => setEditFormData({...editFormData, payment_method: e.target.value})}
                      >
                        <option value="cash">Cash</option>
                        <option value="card">Card</option>
                        <option value="mobile_money">Mobile Money</option>
                        <option value="bank_transfer">Bank Transfer</option>
                      </select>
                    </div>
                  </div>
                </div>

                <div className="form-section">
                  <h3>Items</h3>
                  <div className="items-edit-list">
                    {editFormData.items?.map((item, index) => (
                      <div key={index} className="edit-item-row">
                        <div className="item-info">
                          <div className="item-name">{item.product_name}</div>
                          <div className="item-unit">{item.unit_name}</div>
                          <div className="item-price">{formatCurrency(item.unit_price)} MGA each</div>
                        </div>
                        <div className="item-quantity">
                          <label>Quantity:</label>
                          <input
                            type="number"
                            min="0"
                            step="0.01"
                            value={item.quantity}
                            onChange={(e) => handleEditItemQuantity(index, e.target.value)}
                          />
                        </div>
                        <div className="item-total">
                          {formatCurrency(item.total_price)} MGA
                        </div>
                        <div className="item-actions">
                          <Button
                            size="small"
                            variant="danger"
                            onClick={() => removeEditItem(index)}
                          >
                            Remove
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>

                  <div className="edit-totals">
                    <div className="total-row">
                      <span className="label">Subtotal:</span>
                      <span className="value">
                        {formatCurrency(editFormData.items?.reduce((sum, item) => sum + parseFloat(item.total_price || 0), 0))} MGA
                      </span>
                    </div>
                    <div className="total-row total">
                      <span className="label">Total:</span>
                      <span className="value">
                        {formatCurrency(editFormData.items?.reduce((sum, item) => sum + parseFloat(item.total_price || 0), 0))} MGA
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <Button
                variant="outline"
                onClick={() => {
                  setShowEditModal(false);
                  setEditingSale(null);
                  setEditFormData({});
                }}
              >
                Cancel
              </Button>
              <Button
                variant="primary"
                onClick={saveEditedSale}
                disabled={!editFormData.items || editFormData.items.length === 0}
              >
                Save Changes
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PendingSales;
