import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import api from '../services/api';
import './Reports.css';

const Reports = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [reports, setReports] = useState([]);
  const [selectedReportType, setSelectedReportType] = useState('');
  const [reportFilters, setReportFilters] = useState({
    start_date: '',
    end_date: '',
    product_id: '',
    category_id: '',
    customer_name: ''
  });
  const [generatedReport, setGeneratedReport] = useState(null);
  const [showFilters, setShowFilters] = useState(false);

  const reportTypes = [
    {
      id: 'sales',
      name: 'Sales Report',
      description: 'Detailed sales analysis with revenue, cost, and profit breakdown',
      icon: 'ðŸ’°',
      color: '#10b981'
    },
    {
      id: 'inventory',
      name: 'Inventory Report',
      description: 'Current inventory status, stock levels, and valuation',
      icon: 'ðŸ“¦',
      color: '#8b5cf6'
    },
    {
      id: 'profit_loss',
      name: 'Profit & Loss Report',
      description: 'Financial performance analysis with profit margins',
      icon: 'ðŸ“Š',
      color: '#3b82f6'
    },
    {
      id: 'stock_movement',
      name: 'Stock Movement Report',
      description: 'Track all stock movements, purchases, and sales',
      icon: 'ðŸ“ˆ',
      color: '#f59e0b'
    },
    {
      id: 'customer',
      name: 'Customer Report',
      description: 'Customer analysis and purchase behavior',
      icon: 'ðŸ‘¥',
      color: '#06b6d4'
    }
  ];

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      setLoading(true);
      const response = await api.get('/reports/');
      setReports(response.data);
    } catch (err) {
      setError('Failed to load reports');
      console.error('Reports error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleReportTypeSelect = (reportType) => {
    setSelectedReportType(reportType);
    setShowFilters(true);
    setGeneratedReport(null);
    setError('');
    setSuccess('');
  };

  const handleFilterChange = (field, value) => {
    setReportFilters(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const generateReport = async () => {
    if (!selectedReportType) {
      setError('Please select a report type');
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');

      const params = new URLSearchParams();

      // Add filters to params
      Object.entries(reportFilters).forEach(([key, value]) => {
        if (value) {
          params.append(key, value);
        }
      });

      let endpoint = '';
      switch (selectedReportType) {
        case 'sales':
          endpoint = '/reports/sales/';
          break;
        case 'inventory':
          endpoint = '/reports/inventory/';
          break;
        case 'stock_movement':
          endpoint = '/reports/stock-movements/';
          break;
        default:
          setError('Report type not implemented yet');
          return;
      }

      const url = params.toString() ? `${endpoint}?${params.toString()}` : endpoint;
      const response = await api.get(url);

      setGeneratedReport({
        type: selectedReportType,
        data: response.data,
        generated_at: new Date().toISOString()
      });

      setSuccess('Report generated successfully!');
    } catch (err) {
      setError(`Failed to generate report: ${err.response?.data?.detail || err.message}`);
      console.error('Report generation error:', err);
    } finally {
      setLoading(false);
    }
  };

  const exportReport = async (format) => {
    if (!generatedReport) {
      setError('No report to export');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // Create a new report record
      const reportData = {
        name: `${reportTypes.find(r => r.id === selectedReportType)?.name} - ${new Date().toLocaleDateString()}`,
        report_type: selectedReportType,
        description: `Generated report with filters: ${JSON.stringify(reportFilters)}`,
        parameters: reportFilters
      };

      const response = await api.post('/reports/', reportData);

      setSuccess(`Report exported as ${format.toUpperCase()} successfully!`);
      fetchReports(); // Refresh reports list
    } catch (err) {
      setError(`Failed to export report: ${err.response?.data?.detail || err.message}`);
      console.error('Export error:', err);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const renderReportData = () => {
    if (!generatedReport) return null;

    const { type, data } = generatedReport;

    switch (type) {
      case 'sales':
        return (
          <div className="report-data">
            <h3>Sales Report Data</h3>
            <div className="sales-summary">
              <div className="summary-card">
                <h4>Total Sales</h4>
                <p>${data.total_sales?.toFixed(2) || '0.00'}</p>
              </div>
              <div className="summary-card">
                <h4>Total Cost</h4>
                <p>${data.total_cost?.toFixed(2) || '0.00'}</p>
              </div>
              <div className="summary-card">
                <h4>Total Profit</h4>
                <p>${data.total_profit?.toFixed(2) || '0.00'}</p>
              </div>
              <div className="summary-card">
                <h4>Profit Margin</h4>
                <p>{data.profit_margin?.toFixed(1) || '0.0'}%</p>
              </div>
            </div>

            {data.sales_by_product && (
              <div className="sales-details">
                <h4>Sales by Product</h4>
                <div className="table-container">
                  <table>
                    <thead>
                      <tr>
                        <th>Product</th>
                        <th>Quantity Sold</th>
                        <th>Revenue</th>
                        <th>Cost</th>
                        <th>Profit</th>
                      </tr>
                    </thead>
                    <tbody>
                      {data.sales_by_product.map((item, index) => (
                        <tr key={index}>
                          <td>{item.product_name}</td>
                          <td>{item.quantity_sold}</td>
                          <td>${item.revenue?.toFixed(2) || '0.00'}</td>
                          <td>${item.cost?.toFixed(2) || '0.00'}</td>
                          <td>${item.profit?.toFixed(2) || '0.00'}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        );

      case 'inventory':
        return (
          <div className="report-data">
            <h3>Inventory Report Data</h3>
            <div className="inventory-summary">
              <div className="summary-card">
                <h4>Total Products</h4>
                <p>{data.total_products || 0}</p>
              </div>
              <div className="summary-card">
                <h4>Total Value</h4>
                <p>${data.total_value?.toFixed(2) || '0.00'}</p>
              </div>
              <div className="summary-card">
                <h4>Low Stock Items</h4>
                <p>{data.low_stock_count || 0}</p>
              </div>
              <div className="summary-card">
                <h4>Out of Stock</h4>
                <p>{data.out_of_stock_count || 0}</p>
              </div>
            </div>

            {data.products && (
              <div className="inventory-details">
                <h4>Product Details</h4>
                <div className="table-container">
                  <table>
                    <thead>
                      <tr>
                        <th>Product</th>
                        <th>SKU</th>
                        <th>Stock</th>
                        <th>Unit Price</th>
                        <th>Total Value</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {data.products.map((product, index) => (
                        <tr key={index}>
                          <td>{product.name}</td>
                          <td>{product.sku}</td>
                          <td>{product.stock_quantity}</td>
                          <td>${product.price?.toFixed(2) || '0.00'}</td>
                          <td>${(product.stock_quantity * product.price)?.toFixed(2) || '0.00'}</td>
                          <td>
                            <span className={`status ${product.stock_quantity === 0 ? 'out-of-stock' : product.stock_quantity < 10 ? 'low-stock' : 'in-stock'}`}>
                              {product.stock_quantity === 0 ? 'Out of Stock' : product.stock_quantity < 10 ? 'Low Stock' : 'In Stock'}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        );

      case 'stock_movement':
        return (
          <div className="report-data">
            <h3>Stock Movement Report Data</h3>
            {data.movements && (
              <div className="stock-movements">
                <div className="table-container">
                  <table>
                    <thead>
                      <tr>
                        <th>Date</th>
                        <th>Product</th>
                        <th>Type</th>
                        <th>Quantity</th>
                        <th>Unit</th>
                        <th>Reference</th>
                        <th>User</th>
                      </tr>
                    </thead>
                    <tbody>
                      {data.movements.map((movement, index) => (
                        <tr key={index}>
                          <td>{formatDate(movement.created_at)}</td>
                          <td>{movement.product_name}</td>
                          <td>
                            <span className={`movement-type ${movement.movement_type}`}>
                              {movement.movement_type}
                            </span>
                          </td>
                          <td>{movement.quantity}</td>
                          <td>{movement.unit_symbol}</td>
                          <td>{movement.reference_number || '-'}</td>
                          <td>{movement.user_name || '-'}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        );

      default:
        return (
          <div className="report-data">
            <p>Report data format not implemented yet.</p>
            <pre>{JSON.stringify(data, null, 2)}</pre>
          </div>
        );
    }
  };

  if (loading && !generatedReport) {
    return (
      <div className="reports">
        <div className="reports-loading">
          <div className="spinner"></div>
          <span>Loading reports...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="reports">
      <div className="reports-header">
        <h1>Reports</h1>
        <p>Generate detailed reports and export them for analysis</p>
      </div>

      {error && (
        <div className="alert alert-error">
          <span>âš ï¸</span>
          {error}
        </div>
      )}

      {success && (
        <div className="alert alert-success">
          <span>âœ…</span>
          {success}
        </div>
      )}

      <div className="reports-content">
        {/* Report Type Selection */}
        <div className="report-types-section">
          <h2>Select Report Type</h2>
          <div className="report-types-grid">
            {reportTypes.map((reportType) => (
              <div
                key={reportType.id}
                className={`report-type-card ${selectedReportType === reportType.id ? 'selected' : ''}`}
                onClick={() => handleReportTypeSelect(reportType.id)}
              >
                <div className="report-type-icon" style={{ backgroundColor: reportType.color }}>
                  {reportType.icon}
                </div>
                <div className="report-type-content">
                  <h3>{reportType.name}</h3>
                  <p>{reportType.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Filters Section */}
        {showFilters && (
          <div className="filters-section">
            <h2>Report Filters</h2>
            <div className="filters-grid">
              <div className="filter-group">
                <label>Start Date</label>
                <input
                  type="date"
                  value={reportFilters.start_date}
                  onChange={(e) => handleFilterChange('start_date', e.target.value)}
                />
              </div>
              <div className="filter-group">
                <label>End Date</label>
                <input
                  type="date"
                  value={reportFilters.end_date}
                  onChange={(e) => handleFilterChange('end_date', e.target.value)}
                />
              </div>
              <div className="filter-group">
                <label>Product ID</label>
                <input
                  type="text"
                  placeholder="Enter product ID"
                  value={reportFilters.product_id}
                  onChange={(e) => handleFilterChange('product_id', e.target.value)}
                />
              </div>
              <div className="filter-group">
                <label>Category ID</label>
                <input
                  type="text"
                  placeholder="Enter category ID"
                  value={reportFilters.category_id}
                  onChange={(e) => handleFilterChange('category_id', e.target.value)}
                />
              </div>
              <div className="filter-group">
                <label>Customer Name</label>
                <input
                  type="text"
                  placeholder="Enter customer name"
                  value={reportFilters.customer_name}
                  onChange={(e) => handleFilterChange('customer_name', e.target.value)}
                />
              </div>
            </div>
            <div className="filters-actions">
              <button
                className="btn btn-primary"
                onClick={generateReport}
                disabled={loading}
              >
                {loading ? 'Generating...' : 'Generate Report'}
              </button>
              <button
                className="btn btn-secondary"
                onClick={() => setShowFilters(false)}
              >
                Cancel
              </button>
            </div>
          </div>
        )}

        {/* Generated Report */}
        {generatedReport && (
          <div className="generated-report-section">
            <div className="report-header">
              <h2>
                {reportTypes.find(r => r.id === selectedReportType)?.name}
                <span className="report-date">
                  Generated: {formatDate(generatedReport.generated_at)}
                </span>
              </h2>
              <div className="report-actions">
                <button
                  className="btn btn-success"
                  onClick={() => exportReport('pdf')}
                  disabled={loading}
                >
                  ðŸ“„ Export PDF
                </button>
                <button
                  className="btn btn-info"
                  onClick={() => exportReport('excel')}
                  disabled={loading}
                >
                  ðŸ“Š Export Excel
                </button>
              </div>
            </div>
            {renderReportData()}
          </div>
        )}

        {/* Previous Reports */}
        <div className="previous-reports-section">
          <h2>Previous Reports</h2>
          {reports.length > 0 ? (
            <div className="reports-list">
              {reports.map((report) => (
                <div key={report.id} className="report-item">
                  <div className="report-info">
                    <h4>{report.name}</h4>
                    <p>{report.description}</p>
                    <div className="report-meta">
                      <span>Type: {report.report_type}</span>
                      <span>Generated: {formatDate(report.generated_at)}</span>
                      <span>By: {report.generated_by_name}</span>
                    </div>
                  </div>
                  <div className="report-actions">
                    {report.file_path && (
                      <button className="btn btn-sm btn-outline">
                        ðŸ“¥ Download
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-data">No previous reports found</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Reports;
